SELECT /* CSPDCP411ReportCSPDCP1416RSelect */ D4.SRECS_DEPT_CD AS SRECS_DEPT_CD /* 심사재심의부서코드 */,
    D6.CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */,
    D4.USER_ID /* 사용자아이디 */,
    D5.USER_NM /* 사용자명 */,
    D4.TPOF_KND_SECD /* 제보종류구분코드 */,
    ( SELECT AS CD_NM
FROM TBDCMACM013C WHERE CMM_CD_DVSN_CD = '1000599' AND CD = D4.TPOF_KND_SECD ) AS CVPT_KND_DVSN_CD_NM , D4.NTFCTN_RCPT_YMD , D4.CLM_SECD , D4.CVLCPT_RCPT_RCPT , D4.CRU_PRCS_PRCS_STTS , ( SELECT CD_NM
FROM TBDCMACM013C WHERE CMM_CD_DVSN_CD = '1000051' AND CD = D4.SLCT_PRCS_PRCS ) AS CVPT_HNDL_DVSN_CD_NM , D4.TERM , D4.RSCH_ASMT_TTL FROM ( SELECT DECODE( D1.TPOF_STTS_SECD, '20', D1.TPOF_RCPT_DEPT_CD, D2.TPOF_PRCS_DEPT_CD ) AS SRECS_DEPT_CD /* 심사재심의부서코드 */,
    DECODE( D1.TPOF_STTS_SECD, '20', D1.TPOF_CLR_ID, D2.TPOF_PRCS_PIC_ID ) AS USER_ID /* 사용자아이디 */,
    ( SELECT AS USR_DFN_TXT_1
FROM TBDCMACM013C D3 WHERE D3.CMM_CD_DVSN_CD = '1000047' AND D3.CD = D1.CRU_KDCD ) AS TPOF_KND_SECD , TO_CHAR(D1.CRU_RCPT_DT, 'YYYYMMDD') AS NTFCTN_RCPT_YMD , D1.CLM_SECD , D1.CVLCPT_RCPT_YR || '-민원-' || LPAD(D1.TPOF_ACTL_RCPT_NO, 5, '0') AS CVLCPT_RCPT_RCPT , D1.CRU_PRCS_PRCS_STTS , D3.SLCT_PRCS_PRCS , ROUND(SYSDATE - D1.CRU_RCPT_DT) AS TERM , D1.RSCH_ASMT_TTL FROM TBCSPDCP101M D1 , TBCSPDCP201M D2 , TBCSPDCP205L D3 WHERE D1.CLM_SECD = D2.CLM_SECD(+) AND D1.CVLCPT_RCPT_YR = D2.CVLCPT_RCPT_YR(+) AND D1.CVLCPT_RCPT_RCPT = D2.CVLCPT_RCPT_RCPT(+) AND D2.CLM_SECD = D3.CLM_SECD(+) AND D2.CVLCPT_RCPT_YR = D3.CVLCPT_RCPT_YR(+) AND D2.CVLCPT_RCPT_RCPT = D3.CVLCPT_RCPT_RCPT(+) AND 1 = D3.FRS_MDM_SQNO(+) AND D1.CLM_SECD = '1' AND D1.CRU_PRCS_PRCS_STTS NOT IN ( '10', '20', '21', '30', '60', '70' ) /* 처리중인 것만 계산 */ AND D1.CRU_RCPT_DT BETWEEN TO_DATE(#STARTDT# || '000000', 'YYYYMMDDHH24MISS') AND TO_DATE(#ENDDT# || '235959', 'YYYYMMDDHH24MISS') AND D2.TPOF_LAST_PRCS_DT BETWEEN TO_DATE(#STARTDT# || '000000', 'YYYYMMDDHH24MISS') AND TO_DATE(#ENDDT# || '235959', 'YYYYMMDDHH24MISS') ) D4 , TBDCMACM001M D5 , TBDCMACM002M D6 WHERE D4.SRECS_DEPT_CD = D6.SRECS_DEPT_CD AND D4.USER_ID = D5.USER_ID ORDER BY SRECS_DEPT_CD, USER_NM