SELECT /* CSPDCP411ReportCSPDCP1418RSelect */ D4.USER_ID AS USER_ID /* 사용자아이디 */,
    D5.USER_NM /* 사용자명 */,
    D4.TPOF_KND_SECD /* 제보종류구분코드 */,
    ( SELECT AS CD_NM
FROM TBDCMACM013C WHERE CMM_CD_DVSN_CD = '1000599' AND CD = D4.TPOF_KND_SECD ) AS CVPT_KND_DVSN_CD_NM , D4.CRU_RCPT_PATH_SECD , ( SELECT CD_NM
FROM TBDCMACM013C WHERE CMM_CD_DVSN_CD = '1000056' AND CD = D4.CRU_RCPT_PATH_SECD ) AS ACP_RUT_CD_NM , D4.CVLCPT_RCPT_RCPT , D4.NTFCTN_RCPT_YMD , ( SELECT CD_NM
FROM TBDCMACM013C WHERE CMM_CD_DVSN_CD = '1000361' AND CD = D4.CRU_PRCS_PRCS_STTS ) AS CVPT_HNDL_STA_CD_NM , D4.CRU_PRCS_PRCS_STTS , D4.TPOF_CVLPR_NM , D4.PROF_RBPRSN_INST_NM , D4.RSCH_ASMT_TTL FROM ( SELECT DECODE( D1.TPOF_STTS_SECD, '20', D1.TPOF_RCPT_DEPT_CD, D2.TPOF_PRCS_DEPT_CD ) AS SRECS_DEPT_CD /* 심사재심의부서코드 */,
    DECODE( D1.TPOF_STTS_SECD, '20', D1.TPOF_CLR_ID, D2.TPOF_PRCS_PIC_ID ) AS USER_ID /* 사용자아이디 */,
    ( SELECT AS USR_DFN_TXT_1
FROM TBDCMACM013C D3 WHERE D3.CMM_CD_DVSN_CD = '1000047' AND D3.CD = D1.CRU_KDCD ) AS TPOF_KND_SECD , TO_CHAR(D1.CRU_RCPT_DT, 'YYYYMMDD') AS NTFCTN_RCPT_YMD , D1.CVLCPT_RCPT_YR || '-민원-' || LPAD(D1.TPOF_ACTL_RCPT_NO, 5, '0') AS CVLCPT_RCPT_RCPT , D1.CRU_PRCS_PRCS_STTS , D3.SLCT_PRCS_PRCS , D7.PROF_RBPRSN_INST_NM , D1.RSCH_ASMT_TTL , D1.CRU_RCPT_PATH_SECD , D1.SLCT_CLMT_KDCD , ( CASE WHEN D1.SLCT_CLMT_KDCD IN ('1', '4') THEN D1.TPOF_CVLPR_NM ELSE D1.SLCT_CVLPR_RPRSV_NM END ) AS TPOF_CVLPR_NM FROM TBCSPDCP101M D1 , TBCSPDCP201M D2 , TBCSPDCP205L D3 , ( SELECT D5.CVLCPT_CLM_SECD /* 민원청구구분코드 */,
    D5.TPOF_RCPT_YR /* 제보접수년도 */,
    D5.TPOF_RCPT_NO /* 제보접수번호 */,
    AGGR_CONCAT( DECODE(D5.TPOF_OCRN_INST_CD,'10',D6.PROF_RBPRSN_INST_NM,'20',D5.TPOF_INST_NM), ', ') AS PROF_RBPRSN_INST_NM /* 증명책임자기관명 */
FROM TBCSPDCP206L D5 , TBDCMACM015M D6 WHERE D5.INST_CD = D6.INST_CD AND D5.CVLCPT_INST_DSCTN_SECD = '1' GROUP BY D5.CLM_SECD, D5.CVLCPT_RCPT_YR, D5.CVLCPT_RCPT_RCPT ) D7 WHERE D1.CLM_SECD = D2.CLM_SECD(+) AND D1.CVLCPT_RCPT_YR = D2.CVLCPT_RCPT_YR(+) AND D1.CVLCPT_RCPT_RCPT = D2.CVLCPT_RCPT_RCPT(+) AND D2.CLM_SECD = D3.CLM_SECD(+) AND D2.CVLCPT_RCPT_YR = D3.CVLCPT_RCPT_YR(+) AND D2.CVLCPT_RCPT_RCPT = D3.CVLCPT_RCPT_RCPT(+) AND 1 = D3.FRS_MDM_SQNO(+) AND D1.CLM_SECD = D7.CLM_SECD(+) AND D1.CVLCPT_RCPT_YR = D7.CVLCPT_RCPT_YR(+) AND D1.CVLCPT_RCPT_RCPT = D7.CVLCPT_RCPT_RCPT(+) AND D1.CLM_SECD = '1' AND D1.CRU_PRCS_PRCS_STTS IN ( '30', '40', '41', '50', '60' ,'70') /* 처리중, 시행문작성중, 조사결과, 처리완료 중 담당자가 있는 것만 계산 */ AND D1.CRU_RCPT_DT BETWEEN TO_DATE(#STARTDT# || '000000', 'YYYYMMDDHH24MISS') AND TO_DATE(#ENDDT# || '235959', 'YYYYMMDDHH24MISS') AND D2.TPOF_LAST_PRCS_DT BETWEEN TO_DATE(#STARTDT# || '000000', 'YYYYMMDDHH24MISS') AND TO_DATE(#ENDDT# || '235959', 'YYYYMMDDHH24MISS') ) D4 , TBDCMACM001M D5 WHERE D4.USER_ID = D5.USER_ID ORDER BY USER_NM