SELECT DECODE(A.TPOF_CLMT_KDCD,'1',A.TPOF_CVLPR_NM,'2',A.TPOF_CVLPR_RPRSV_NM,'3',A.TPOF_CVLPR_RPRSV_NM,'4',A.TPOF_CVLPR_NM) AS TPOF_CLMT_KDCD /* 제보청구인종류코드 */,
    OTSD_EXPRT_ADDR || DECODE(OTSD_EXPRT_ADDR, NULL, '', ',') ||CRU_CVLPR_DADDR AS OTSD_EXPRT_ADDR /* 외부전문가주소 */,
    DECODE(CRU_ZIP , NULL, '-', SUBSTR(CRU_ZIP, 0, 3) || ' - ' || SUBSTR(CRU_ZIP, 4, 6) ) AS CRU_ZIP /* 부패우편번호 */
FROM TB_CSPDCP101M A, TB_CSPDCP301M B, TB_CSPDCP201M C WHERE 1=1 AND A.CVLCPT_CLM_SECD = B.CVLCPT_CLM_SECD AND A.TPOF_RCPT_YR = B.TPOF_RCPT_YR AND A.TPOF_RCPT_NO = B.TPOF_RCPT_NO AND A.CVLCPT_CLM_SECD = C.CVLCPT_CLM_SECD AND A.TPOF_RCPT_YR = C.TPOF_RCPT_YR AND A.TPOF_RCPT_NO = C.TPOF_RCPT_NO AND A.CVLCPT_CLM_SECD = '1' AND B.TPOF_AVTSMT_SECD IN ('A','B','F') AND B.TPOF_OPM_ATRZ_STTS_SECD = '30' AND A.TPOF_KDCD = #CRU_KDCD# AND TO_CHAR(B.ONR_ATRZ_CMPTN_DT,'YYYYMMDD') BETWEEN #STARTDT# AND #ENDDT# AND A.TPOF_RCPT_DEPT_CD = #CRU_RCPT_DEPT_CD# AND C.TPOF_PRCS_DEPT_CD = #ETC_LBSVC_MNG_DEPT_CD# ORDER BY A.CVLCPT_CLM_SECD, A.TPOF_RCPT_YR, A.TPOF_RCPT_NO