SELECT NVL(B.TPOF_ONR_OPM_NO, ' ') AS FRS_MDM_SQNO /* 포렌식매체순번 */,
    D.RSCH_ASMT_TTL /* 연구과제제목 */,
    TO_CHAR(B.ONR_ATRZ_CMPTN_DT, 'YYYY.MM.DD') AS TO_CHAR,
    DECODE(A.TPOF_CLMT_KDCD,'1',A.TPOF_CVLPR_NM,'2',A.TPOF_CVLPR_RPRSV_NM,'3',A.TPOF_CVLPR_RPRSV_NM,'4',A.TPOF_CVLPR_NM) AS TPOF_CLMT_KDCD /* 제보청구인종류코드 */,
    '우편' AS SLCT_CLMT_KDCD /* 청탁청구인종류코드 */,
    F_CODE_NM('1000056', A.TPOF_RCPT_PATH_SECD) AS F_CODE_NM,
    F_CODE_NM('1000051',(SELECT AS SLCT_PRCS_PRCS /* 청탁처리구분코드 */
FROM TB_CSPDCP205L WHERE CLM_SECD = A.CVLCPT_CLM_SECD AND CVLCPT_RCPT_YR = A.TPOF_RCPT_YR AND CVLCPT_RCPT_RCPT = A.TPOF_RCPT_NO AND FRS_MDM_SQNO = ( SELECT MIN(FRS_MDM_SQNO) AS FRS_MDM_SQNO /* 포렌식매체순번 */
FROM TB_CSPDCP205L X WHERE X.CLM_SECD = A.CVLCPT_CLM_SECD AND X.CVLCPT_RCPT_YR = A.TPOF_RCPT_YR AND X.CVLCPT_RCPT_RCPT = A.TPOF_RCPT_NO ) )) AS ETC1 ,B.TPOF_RCPT_YR || '-민원-' || A.TPOF_ACTL_RCPT_NO AS ETC2 FROM TB_CSPDCP101M A, TB_CSPDCP301M B, TB_CSPDCP201M C, TBDCMACM101M D WHERE 1=1 AND A.CVLCPT_CLM_SECD = B.CVLCPT_CLM_SECD AND A.TPOF_RCPT_YR = B.TPOF_RCPT_YR AND A.TPOF_RCPT_NO = B.TPOF_RCPT_NO AND A.CVLCPT_CLM_SECD = C.CVLCPT_CLM_SECD AND A.TPOF_RCPT_YR = C.TPOF_RCPT_YR AND A.TPOF_RCPT_NO = C.TPOF_RCPT_NO AND B.TPOF_ONR_DOC_MNG_CARD_ID = D.ONR_DOC_MNG_CARD_ID AND A.CVLCPT_CLM_SECD = '1' AND B.TPOF_AVTSMT_SECD IN ('A','B','F') AND B.TPOF_OPM_ATRZ_STTS_SECD = '30' AND A.TPOF_KDCD = #CRU_KDCD# AND TO_CHAR(B.ONR_ATRZ_CMPTN_DT,'YYYYMMDD') BETWEEN #STARTDT# AND #ENDDT# AND A.TPOF_RCPT_DEPT_CD = #CRU_RCPT_DEPT_CD# AND C.TPOF_PRCS_DEPT_CD = #ETC_LBSVC_MNG_DEPT_CD# ORDER BY A.CVLCPT_CLM_SECD, A.TPOF_RCPT_YR, A.TPOF_ACTL_RCPT_NO