SELECT ROWNUM,
    DECODE(A.TPOF_ACTL_RCPT_NO,NULL,'',A.TPOF_RCPT_YR||'-민원-'||A.TPOF_RCPT_NO) AS NTFCTN_RCPT_SQNO /* 통보접수순번 */,
    TO_CHAR(A.FRST_REG_DT,'YYYY-MM-DD') AS TO_CHAR,
    TO_CHAR(A.TPOF_RCPT_DT,'YYYY-MM-DD') AS CRU_RCPT_DT /* 부패접수일시 */,
    A.TPOF_NM /* 제보명 */,
    DECODE(A.TPOF_CLMT_KDCD,'1',A.TPOF_CVLPR_NM,'2',A.TPOF_CVLPR_RPRSV_NM,'3',A.TPOF_CVLPR_RPRSV_NM,'4',A.TPOF_CVLPR_NM) AS TPOF_CVLPR_NM /* 제보민원인명 */,
    F_DPT_INFO(A.TPOF_RCPT_DEPT_CD,'1') AS F_DPT_INFO,
    F_USR_INFO_BY_ID(A.TPOF_CLR_ID,'2') AS F_USR_INFO_BY_ID,
    F_CODE_NM('1000056',A.TPOF_RCPT_PATH_SECD) AS F_CODE_NM
FROM TB_CSPDCP101M A ,TB_CSPDCP102M B WHERE 1=1 AND A.CLM_SECD = '1' AND A.CLM_SECD = B.CVLCPT_CLM_SECD AND A.CVLCPT_RCPT_YR = B.TPOF_RCPT_YR AND A.CVLCPT_RCPT_RCPT = B.TPOF_RCPT_NO AND TO_CHAR(A.FRST_REG_DT,'YYYYMMDD') &gt;= #strACP_DHS# AND TO_CHAR(A.FRST_REG_DT,'YYYYMMDD') &lt;= #strACP_DHE# AND A.CRU_PRCS_PRCS_STTS = #CRU_PRCS_PRCS_STTS# AND A.RSCH_ASMT_TTL LIKE '%'||#strTIT_NM#||'%' AND B.TPOF_CN LIKE '%'||#strTIT_NM#||'%' AND A.CVLCPT_RCPT_YR = #CVLCPT_RCPT_YR# AND A.CVLCPT_RCPT_RCPT = #CVLCPT_RCPT_RCPT# AND A.CRU_SMG_CVLCPT_APLY_NO = #CRU_SMG_CVLCPT_APLY_NO# AND (A.TPOF_CVLPR_NM LIKE '%'||#strCVPTR_NM#||'%' OR A.SLCT_CVLPR_RPRSV_NM LIKE '%'||#strCVPTR_NM#||'%') AND A.SLCT_CLMT_KDCD IN (SELECT SUBSTR(CODE,INSTR(CODE,',',1,LEVEL)+1,INSTR(CODE,',',1,LEVEL+1)-INSTR(CODE,',',1,LEVEL) -1) AS SUBSTR
FROM DUAL A CROSS JOIN(SELECT ','|| #strREQR_KND_CD# ||',' AS #STRREQR_KND_CD#
FROM DUAL) B CONNECT BY LEVEL&lt;=LENGTH(CODE)-LENGTH(REPLACE(CODE,','))-1 ) AND A.CRU_KDCD IN (SELECT SUBSTR(CODE,INSTR(CODE,',',1,LEVEL)+1,INSTR(CODE,',',1,LEVEL+1)-INSTR(CODE,',',1,LEVEL) -1) AS SUBSTR
FROM DUAL A CROSS JOIN(SELECT ','|| #strCVPT_KND_CD# ||',' AS #STRCVPT_KND_CD#
FROM DUAL) B CONNECT BY LEVEL&lt;=LENGTH(CODE)-LENGTH(REPLACE(CODE,','))-1 ) AND A.CRU_RCPT_PATH_SECD IN (SELECT SUBSTR(CODE,INSTR(CODE,',',1,LEVEL)+1,INSTR(CODE,',',1,LEVEL+1)-INSTR(CODE,',',1,LEVEL) -1) AS SUBSTR
FROM DUAL A CROSS JOIN(SELECT ','|| #strACP_RUT_CD# ||',' AS #STRACP_RUT_CD#
FROM DUAL) B CONNECT BY LEVEL&lt;=LENGTH(CODE)-LENGTH(REPLACE(CODE,','))-1 ) AND A.CRU_MNG_TPCD IN (SELECT SUBSTR(CODE,INSTR(CODE,',',1,LEVEL)+1,INSTR(CODE,',',1,LEVEL+1)-INSTR(CODE,',',1,LEVEL) -1) AS SUBSTR
FROM DUAL A CROSS JOIN(SELECT ','|| #strMNG_TYP_CD# ||',' AS #STRMNG_TYP_CD#
FROM DUAL) B CONNECT BY LEVEL&lt;=LENGTH(CODE)-LENGTH(REPLACE(CODE,','))-1 ) ORDER BY A.CRU_RCPT_DT DESC