SELECT A.CVLCPT_CLM_SECD /* 민원청구구분코드 */,
    A.TPOF_RCPT_YR /* 제보접수년도 */,
    A.TPOF_RCPT_NO /* 제보접수번호 */,
    A.TPOF_ACTL_RCPT_NO /* 제보실제접수번호 */,
    DECODE(A.TPOF_ACTL_RCPT_NO,NULL,NULL,A.TPOF_RCPT_YR||'-민원-'||A.TPOF_ACTL_RCPT_NO) AS NTFCTN_RCPT_SQNO /* 통보접수순번 */,
    A.TPOF_KDCD /* 제보종류코드 */,
    A.TPOF_NM /* 제보명 */,
    A.TPOF_CLMT_KDCD /* 제보청구인종류코드 */,
    A.TPOF_RCPT_PATH_SECD /* 제보접수경로구분코드 */,
    DECODE(A.TPOF_CLMT_KDCD,'1',A.TPOF_CVLPR_NM,'2',A.TPOF_CVLPR_RPRSV_NM,'3',A.TPOF_CVLPR_RPRSV_NM,'4',A.TPOF_CVLPR_NM) AS TPOF_CVLPR_NM /* 제보민원인명 */,
    TO_CHAR(A.TPOF_RCPT_DT,'YYYY-MM-DD HH24:MI') AS CRU_RCPT_DT /* 부패접수일시 */,
    A.TPOF_STTS_SECD /* 제보상태구분코드 */,
    A.TPOF_CVLPR_MPNO /* 제보민원인휴대전화번호 */,
    TO_CHAR(A.FRST_REG_DT,'YYYYMMDD') AS TO_CHAR,
    F_DPT_INFO(A.TPOF_RCPT_DEPT_CD,'1') AS F_DPT_INFO,
    F_DPT_INFO(C.TPOF_PRCS_DEPT_CD,'1') AS PRCS_DEPT_NM /* 처리부서명 */,
    F_USR_INFO_BY_ID(C.TPOF_PRCS_PIC_ID,'2') AS SRECS_PRCS_PIC_NM /* 심사재심의처리담당자명 */,
    TO_CHAR(A.TPOF_DISCON_DT,'YYYY-MM-DD HH24:MI') AS TPOF_DISCON_DT /* 제보취하일시 */,
    ( SELECT F_CODE_NM('1000754', S3.TPOF_PRCS_SECD) AS TPOF_PRCS_SECD /* 제보처리구분코드 */
FROM TB_CSPDCP205L S3 WHERE (S3.CVLCPT_CLM_SECD, S3.TPOF_RCPT_YR, S3.TPOF_RCPT_NO, S3.TPOF_SQNO) IN ( SELECT S4.CVLCPT_CLM_SECD /* 민원청구구분코드 */,
    S4.TPOF_RCPT_YR /* 제보접수년도 */,
    S4.TPOF_RCPT_NO /* 제보접수번호 */,
    MIN(S4.TPOF_SQNO) AS TPOF_SQNO /* 제보순번 */
FROM TB_CSPDCP205L S4 WHERE A.CLM_SECD = S4.CLM_SECD AND A.CVLCPT_RCPT_YR = S4.CVLCPT_RCPT_YR AND A.CVLCPT_RCPT_RCPT = S4.CVLCPT_RCPT_RCPT GROUP BY S4.CLM_SECD,S4.CVLCPT_RCPT_YR,S4.CVLCPT_RCPT_RCPT ) ) AS CVPT_HNLD_DVSN_NM ,TO_CHAR(D.ONR_ATRZ_CMPTN_DT,'YYYY-MM-DD') AS ONR_ATRZ_CMPTN_DT ,( SELECT TO_CHAR(TO_DATE(TO_CHAR(S2.ONR_ATRZ_CMPTN_DT, 'YYYYMMDD'), 'YYYYMMDD') + NVL( S1.USR_DFN_TXT_2, 30), 'YYYY-MM-DD') AS ONR_ATRZ_CMPTN_DT /* 온나라결재완료일시 */
FROM TBDCMACM013C S1, TB_CSPDCP205L D3, TB_CSPDCP301M S2 WHERE S1.CMM_CD_DVSN_CD = '1000754' AND S1.CD != '000000000' AND S1.CD = D3.SLCT_PRCS_PRCS AND A.CLM_SECD = D3.CLM_SECD AND A.CVLCPT_RCPT_YR = D3.CVLCPT_RCPT_YR AND A.CVLCPT_RCPT_RCPT = D3.CVLCPT_RCPT_RCPT AND A.CLM_SECD = S2.CLM_SECD AND A.CVLCPT_RCPT_YR = S2.CVLCPT_RCPT_YR AND A.CVLCPT_RCPT_RCPT = S2.CVLCPT_RCPT_RCPT AND S2.SLCT_AVTSMT_AVTSMT = 'C' AND S2.CVLCPT_ATRZ_PRCS_STTS_SECD = '30' AND (D3.CLM_SECD, D3.CVLCPT_RCPT_RCPT, D3.CVLCPT_RCPT_YR, D3.FRS_MDM_SQNO ) IN ( SELECT Z.CVLCPT_CLM_SECD /* 민원청구구분코드 */,
    Z.TPOF_RCPT_NO /* 제보접수번호 */,
    Z.TPOF_RCPT_YR /* 제보접수년도 */,
    MIN(Z.TPOF_SQNO) AS TPOF_SQNO /* 제보순번 */
FROM TB_CSPDCP205L Z WHERE A.CLM_SECD = Z.CLM_SECD AND A.CVLCPT_RCPT_RCPT = Z.CVLCPT_RCPT_RCPT AND A.CVLCPT_RCPT_YR = Z.CVLCPT_RCPT_YR GROUP BY Z.CLM_SECD, Z.CVLCPT_RCPT_RCPT, Z.CVLCPT_RCPT_YR )) AS HNDL_LIMIT__DT , F_CODE_NM('1000599',C.TPOF_KND_SECD) AS CVPT_KND_DVSN_NM FROM TB_CSPDCP101M A ,TB_CSPDCP102M B ,TB_CSPDCP201M C ,TB_CSPDCP301M D WHERE 1=1 AND A.CLM_SECD = '1' AND A.CLM_SECD = B.CVLCPT_CLM_SECD AND A.CVLCPT_RCPT_YR = B.TPOF_RCPT_YR AND A.CVLCPT_RCPT_RCPT = B.TPOF_RCPT_NO AND A.CLM_SECD = C.CVLCPT_CLM_SECD(+) AND A.CVLCPT_RCPT_YR = C.TPOF_RCPT_YR(+) AND A.CVLCPT_RCPT_RCPT = C.TPOF_RCPT_NO(+) AND A.CLM_SECD = D.CVLCPT_CLM_SECD(+) AND A.CVLCPT_RCPT_YR = D.TPOF_RCPT_YR(+) AND A.CVLCPT_RCPT_RCPT = D.TPOF_RCPT_NO(+) AND D.TPOF_AVTSMT_SECD(+) = 'A' AND TO_CHAR(A.FRST_REG_DT,'YYYYMMDD') &gt;= #strACP_DHS# AND TO_CHAR(A.FRST_REG_DT,'YYYYMMDD') &lt;= #strACP_DHE# AND A.CRU_PRCS_PRCS_STTS = #CRU_PRCS_PRCS_STTS# AND A.RSCH_ASMT_TTL LIKE '%'||#strTIT_NM#||'%' AND B.TPOF_CN LIKE '%'||#strTIT_NM#||'%' AND A.CRU_HMPG_APLY_NO = #CRU_HMPG_APLY_NO# AND A.CVLCPT_RCPT_YR = #CVLCPT_RCPT_YR# AND A.TPOF_ACTL_RCPT_NO = #TPOF_ACTL_RCPT_NO# AND A.CRU_SMG_CVLCPT_APLY_NO = #CRU_SMG_CVLCPT_APLY_NO# AND (A.TPOF_CVLPR_NM LIKE '%'||#strCVPTR_NM#||'%' OR A.SLCT_CVLPR_RPRSV_NM LIKE '%'||#strCVPTR_NM#||'%') AND A.SLCT_CLMT_KDCD IN (SELECT SUBSTR(CODE,INSTR(CODE,',',1,LEVEL)+1,INSTR(CODE,',',1,LEVEL+1)-INSTR(CODE,',',1,LEVEL) -1) AS SUBSTR
FROM DUAL A CROSS JOIN(SELECT ','|| #strREQR_KND_CD# ||',' AS #STRREQR_KND_CD#
FROM DUAL) B CONNECT BY LEVEL&lt;=LENGTH(CODE)-LENGTH(REPLACE(CODE,','))-1 ) AND A.CRU_KDCD IN (SELECT SUBSTR(CODE,INSTR(CODE,',',1,LEVEL)+1,INSTR(CODE,',',1,LEVEL+1)-INSTR(CODE,',',1,LEVEL) -1) AS SUBSTR
FROM DUAL A CROSS JOIN(SELECT ','|| #strCVPT_KND_CD# ||',' AS #STRCVPT_KND_CD#
FROM DUAL) B CONNECT BY LEVEL&lt;=LENGTH(CODE)-LENGTH(REPLACE(CODE,','))-1 ) AND A.CRU_RCPT_PATH_SECD IN (SELECT SUBSTR(CODE,INSTR(CODE,',',1,LEVEL)+1,INSTR(CODE,',',1,LEVEL+1)-INSTR(CODE,',',1,LEVEL) -1) AS SUBSTR
FROM DUAL A CROSS JOIN(SELECT ','|| #strACP_RUT_CD# ||',' AS #STRACP_RUT_CD#
FROM DUAL) B CONNECT BY LEVEL&lt;=LENGTH(CODE)-LENGTH(REPLACE(CODE,','))-1 ) AND A.CRU_MNG_TPCD IN (SELECT SUBSTR(CODE,INSTR(CODE,',',1,LEVEL)+1,INSTR(CODE,',',1,LEVEL+1)-INSTR(CODE,',',1,LEVEL) -1) AS SUBSTR
FROM DUAL A CROSS JOIN(SELECT ','|| #strMNG_TYP_CD# ||',' AS #STRMNG_TYP_CD#
FROM DUAL) B CONNECT BY LEVEL&lt;=LENGTH(CODE)-LENGTH(REPLACE(CODE,','))-1 ) ORDER BY A.CVLCPT_RCPT_YR DESC , A.TPOF_ACTL_RCPT_NO DESC