SELECT D2.TPOF_ACTL_RCPT_NO AS TPOF_ACTL_RCPT_NO /* 제보실제접수번호 */,
    NVL(F_DPT_INFO (D1.ETC_LBSVC_MNG_DEPT_CD, '1'),' ') AS PRCS_DEPT_NM /* 처리부서명 */,
    TO_CHAR(D2.TPOF_RCPT_DT, 'YYYY-MM-DD') AS NTFCTN_RCPT_YMD /* 통보접수일자 */,
    D2.TPOF_NM /* 제보명 */,
    NVL(DECODE(D2.TPOF_CLMT_KDCD,'1',D2.TPOF_CVLPR_NM,'2',D2.TPOF_CVLPR_RPRSV_NM,'3',D2.TPOF_CVLPR_RPRSV_NM,'4',D2.TPOF_CVLPR_NM),' ') AS TPOF_CVLPR_NM /* 제보민원인명 */,
    F_DPT_INFO (D2.TPOF_RCPT_DEPT_CD, '1') AS F_DPT_INFO,
    NVL(F_USR_INFO_BY_ID (D1.PRCS_PIC_ID, '2'),' ') AS SRECS_PRCS_PIC_NM /* 심사재심의처리담당자명 */,
    ' ' AS RMRK_CN /* 비고내용 */,
    ' ' AS U,
    F_CODE_NM ('1000056', D2.TPOF_RCPT_PATH_SECD) AS F_CODE_NM,
    ' ' AS AA
FROM TB_CSPDCP101M D2 ,(SELECT *
FROM TBCSPDCP202H D1 WHERE ETC_LBSVC_MNG_DEPT_CD IS NOT NULL AND (D1.CLM_SECD, D1.CVLCPT_RCPT_YR, D1.CVLCPT_RCPT_RCPT,D1.CVLCPT_HNDT_HNDT_HSTRY) IN (SELECT S1.CVLCPT_CLM_SECD /* 민원청구구분코드 */,
    S1.TPOF_RCPT_YR /* 제보접수년도 */,
    S1.TPOF_RCPT_NO /* 제보접수번호 */,
    MIN(CVLCPT_HNDT_HNDT_HSTRY) AS CVLCPT_HNDT_HNDT_HSTRY /* 민원배부이력순번 */
FROM TBCSPDCP202H S1 WHERE S1.ETC_LBSVC_MNG_DEPT_CD IS NOT NULL GROUP BY S1.CLM_SECD, S1.CVLCPT_RCPT_YR, S1.CVLCPT_RCPT_RCPT) ) D1 WHERE D2.CVLCPT_CLM_SECD ='1' AND D2.CVLCPT_CLM_SECD = D1.CLM_SECD AND D2.TPOF_RCPT_YR = D1.CVLCPT_RCPT_YR AND D2.TPOF_RCPT_NO = D1.CVLCPT_RCPT_RCPT AND TO_CHAR(D1.FRST_REG_DT, 'YYYYMMDD') = #strDt# AND D1.ETC_LBSVC_MNG_DEPT_CD IS NOT NULL AND D2.TPOF_RCPT_DEPT_CD IN ('071100','071110') AND D2.TPOF_RCPT_DEPT_CD IN ('071100','071110') AND D2.TPOF_RCPT_DEPT_CD = #CRU_HNDT_DEPT_CD# AND (D1.CLM_SECD, D1.CVLCPT_RCPT_YR, D1.CVLCPT_RCPT_RCPT,D1.CVLCPT_HNDT_HNDT_HSTRY) IN (SELECT S1.CVLCPT_CLM_SECD /* 민원청구구분코드 */,
    S1.TPOF_RCPT_YR /* 제보접수년도 */,
    S1.TPOF_RCPT_NO /* 제보접수번호 */,
    MIN(CVLCPT_HNDT_HNDT_HSTRY) AS CVLCPT_HNDT_HNDT_HSTRY /* 민원배부이력순번 */
FROM TB_CSPDCP202H S1 WHERE S1.CRU_PRCS_PRCS_STTS IN ('20','30') AND TO_CHAR(S1.FRST_REG_DT, 'YYYYMMDD') = #strDt# AND S1.ETC_LBSVC_MNG_DEPT_CD IS NOT NULL AND S1.CRU_HNDT_DEPT_CD IN ('071100','071110') AND S1.CRU_HNDT_DEPT_CD IN ('071100','071110') AND S1.CRU_HNDT_DEPT_CD = #CRU_HNDT_DEPT_CD# GROUP BY S1.CLM_SECD, S1.CVLCPT_RCPT_YR, S1.CVLCPT_RCPT_RCPT) ORDER BY D1.CVLCPT_RCPT_RCPT DESC