MERGE INTO TB_CSPDCP201M A USING ( SELECT #REQ_DVSN_CD# AS CLM_SECD /* 청구구분코드 */,
    #ACP_YR# AS CVLCPT_RCPT_YR /* 민원접수년도 */,
    #CVPT_ACP_NO# AS CVLCPT_RCPT_RCPT /* 민원접수번호 */
FROM DUAL ) B ON ( A.CVLCPT_CLM_SECD = B.CLM_SECD AND A.TPOF_RCPT_YR = B.CVLCPT_RCPT_YR AND A.TPOF_RCPT_NO = B.CVLCPT_RCPT_RCPT ) WHEN MATCHED THEN UPDATE SET SLCT_LAST_HNDT_DEPT_CD = F_USR_INFO(#S_CNB#,'5') /* 청탁최종배부부서코드 */, SLCT_LAST_HNDT_PIC_ID = F_USR_INFO(#S_CNB#,'1') /* 청탁최종배부담당자아이디 */, CRU_LAST_HNDT_DT = SYSDATE /* 부패최종배부일시 */, ETC_LBSVC_MNG_DEPT_CD = #ETC_LBSVC_MNG_DEPT_CD# /* 기타용역관리부서코드 */, PRCS_PIC_ID = #PRCS_PIC_ID# /* 처리담당자아이디 */, SLCT_BAI_PIC_ID = #SLCT_BAI_PIC_ID# /* 청탁감사원담당자아이디 */, LAST_USER_ID = #LAST_USER_ID# /* 최종사용자아이디 */, LAST_PRCS_DEPT_CD = #LAST_PRCS_DEPT_CD# /* 최종처리부서코드 */, LAST_MENU_ID = #LAST_MENU_ID# /* 최종메뉴아이디 */, LAST_MDFCN_DT = SYSDATE WHEN NOT MATCHED THEN INSERT INSERT ( CLM_SECD ,CVLCPT_RCPT_YR ,CVLCPT_RCPT_RCPT ,SLCT_LAST_HNDT_DEPT_CD ,SLCT_LAST_HNDT_PIC_ID ,CRU_LAST_HNDT_DT ,ETC_LBSVC_MNG_DEPT_CD ,PRCS_PIC_ID ,FRST_USER_ID ,LAST_USER_ID ,LAST_PRCS_DEPT_CD ,LAST_MENU_ID ,FRST_REG_DT ,LAST_MDFCN_DT ,SLCT_BAI_PIC_ID ) VALUES ( #REQ_DVSN_CD# ,#ACP_YR# ,#CVPT_ACP_NO# ,F_USR_INFO(#S_CNB#,'5') ,F_USR_INFO(#S_CNB#,'1') ,SYSDATE ,#ETC_LBSVC_MNG_DEPT_CD# ,#PRCS_PIC_ID# ,F_USR_INFO(#S_CNB#,'1') ,#LAST_USER_ID# ,#LAST_PRCS_DEPT_CD# ,#LAST_MENU_ID# ,SYSDATE ,SYSDATE ,#SLCT_BAI_PIC_ID# ) /* 최종수정일시 */