UPDATE TB_CSPDCP101M SET CRU_PRCS_PRCS_STTS = CASE WHEN #CVPT_WDW_YN# = 'Y' THEN '70' WHEN #CVPT_WDW_YN# = 'N' AND #CVPT_KND_DVSN_CD# = '1' AND ( SELECT Z.TPOF_PRCS_SECD /* 제보처리구분코드 */
FROM TB_CSPDCP205L Z WHERE Z.CLM_SECD = #CLM_SECD# AND Z.CVLCPT_RCPT_YR = #CVLCPT_RCPT_YR# AND Z.CVLCPT_RCPT_RCPT = #CVLCPT_RCPT_RCPT# AND Z.FRS_MDM_SQNO = ( SELECT MIN(FRS_MDM_SQNO) AS FRS_MDM_SQNO /* 포렌식매체순번 */
FROM TB_CSPDCP205L X WHERE X.CLM_SECD = #CLM_SECD# AND X.CVLCPT_RCPT_YR = #CVLCPT_RCPT_YR# AND X.CVLCPT_RCPT_RCPT = #CVLCPT_RCPT_RCPT# ) ) IN ('60') THEN '50' WHEN #CVPT_WDW_YN# = 'N' AND #CVPT_KND_DVSN_CD# = '2' THEN '60' ELSE '60' END WHERE CLM_SECD = #CLM_SECD# AND CVLCPT_RCPT_YR = #CVLCPT_RCPT_YR# AND CVLCPT_RCPT_RCPT = #CVLCPT_RCPT_RCPT#