INSERT INTO TB_CSPDCP202H (CLM_SECD, CVLCPT_RCPT_YR, CVLCPT_RCPT_RCPT, CVLCPT_HNDT_HNDT_HSTRY, CRU_HNDT_DEPT_CD, SLCT_HNDT_PIC_ID, CRU_PRCS_PRCS_STTS, FRST_USER_ID, LAST_USER_ID, LAST_PRCS_DEPT_CD, LAST_MENU_ID, FRST_REG_DT, LAST_MDFCN_DT) SELECT A.CVLCPT_CLM_SECD /* 민원청구구분코드 */,
    A.TPOF_RCPT_YR /* 제보접수년도 */,
    A.TPOF_RCPT_NO /* 제보접수번호 */,
    ( SELECT NVL(MAX(CVLCPT_HNDT_HNDT_HSTRY),0)+1 AS CVLCPT_HNDT_HNDT_HSTRY /* 민원배부이력순번 */
FROM TB_CSPDCP202H Z WHERE 1=1 AND Z.CVLCPT_CLM_SECD = A.CVLCPT_CLM_SECD AND Z.TPOF_RCPT_YR = A.TPOF_RCPT_YR AND Z.TPOF_RCPT_NO = A.TPOF_RCPT_NO ) ,F_USR_INFO(#S_CNB#,'5') ,F_USR_INFO(#S_CNB#,'1') ,( SELECT CRU_PRCS_PRCS_STTS /* 부패처리상태구분코드 */
FROM TB_CSPDCP101M WHERE CLM_SECD = #CLM_SECD# AND CVLCPT_RCPT_YR = #CVLCPT_RCPT_YR# AND CVLCPT_RCPT_RCPT = #CVLCPT_RCPT_RCPT# ) ,F_USR_INFO(#S_CNB#,'1') ,#FNL_USR_ID# ,#FNL_DEAL_DPT_CD# ,#FNL_MNU_ID# ,SYSDATE ,SYSDATE FROM TB_CSPDCP101M A WHERE (A.CVLCPT_CLM_SECD, A.TPOF_RCPT_YR,A.TPOF_RCPT_NO ) IN ( SELECT CVLCPT_MCA_CLM_SECD /* 민원합건청구구분코드 */,
    TPOF_MCA_RCPT_YR /* 제보합건접수년도 */,
    CVLCPT_MCA_CVLCPT_RCPT_NO /* 민원합건민원접수번호 */
FROM TB_CSPDCP105L WHERE CLM_SECD = #CLM_SECD# AND CVLCPT_RCPT_YR = #CVLCPT_RCPT_YR# AND CVLCPT_RCPT_RCPT = #CVLCPT_RCPT_RCPT# )