UPDATE TB_CSPDCP101M A SET A.TPOF_STTS_SECD = #CRU_PRCS_PRCS_STTS# /* 제보상태구분코드 */, A.TPOF_KDCD = (SELECT Z.TPOF_KDCD /* 제보종류코드 */
FROM TB_CSPDCP101M Z WHERE Z.CLM_SECD = #CLM_SECD# AND Z.CVLCPT_RCPT_YR = #CVLCPT_RCPT_YR# AND Z.CVLCPT_RCPT_RCPT = #CVLCPT_RCPT_RCPT#) , A.LAST_USER_ID = #LAST_USER_ID# /* 최종사용자아이디 */, A.LAST_PRCS_DEPT_CD = #LAST_PRCS_DEPT_CD# /* 최종처리부서코드 */, A.LAST_MENU_ID = #LAST_MENU_ID# /* 최종메뉴아이디 */, A.LAST_MDFCN_DT = SYSDATE /* 최종수정일시 */WHERE A.CVLCPT_CLM_SECD = #CLM_SECD# AND A.TPOF_RCPT_YR = #CVLCPT_RCPT_YR# AND A.TPOF_RCPT_NO = #CVLCPT_RCPT_RCPT#