UPDATE TB_CSPDCP201M A SET A.TPOF_KND_SECD = (SELECT Z.TPOF_KND_SECD /* 제보종류구분코드 */
FROM TB_CSPDCP201M Z WHERE Z.CLM_SECD = #CLM_SECD# AND Z.CVLCPT_RCPT_YR = #CVLCPT_RCPT_YR# AND Z.CVLCPT_RCPT_RCPT = #CVLCPT_RCPT_RCPT#) , A.TPOF_PASV_PBADMS_YN = (SELECT Z.TPOF_PASV_PBADMS_YN /* 제보소극행정여부 */
FROM TB_CSPDCP201M Z WHERE Z.CLM_SECD = #CLM_SECD# AND Z.CVLCPT_RCPT_YR = #CVLCPT_RCPT_YR# AND Z.CVLCPT_RCPT_RCPT = #CVLCPT_RCPT_RCPT#) , A.TPOF_RFRNC_YN = (SELECT Z.TPOF_RFRNC_YN /* 제보참조여부 */
FROM TB_CSPDCP201M Z WHERE Z.CLM_SECD = #CLM_SECD# AND Z.CVLCPT_RCPT_YR = #CVLCPT_RCPT_YR# AND Z.CVLCPT_RCPT_RCPT = #CVLCPT_RCPT_RCPT#) , A.TPOF_DOC_ID = (SELECT Z.TPOF_DOC_ID /* 제보문서아이디 */
FROM TB_CSPDCP201M Z WHERE Z.CLM_SECD = #CLM_SECD# AND Z.CVLCPT_RCPT_YR = #CVLCPT_RCPT_YR# AND Z.CVLCPT_RCPT_RCPT = #CVLCPT_RCPT_RCPT#) , A.TPOF_DOC_TPCD = (SELECT Z.TPOF_DOC_TPCD /* 제보문서유형코드 */
FROM TB_CSPDCP201M Z WHERE Z.CLM_SECD = #CLM_SECD# AND Z.CVLCPT_RCPT_YR = #CVLCPT_RCPT_YR# AND Z.CVLCPT_RCPT_RCPT = #CVLCPT_RCPT_RCPT#) WHERE A.CVLCPT_CLM_SECD = #CLM_SECD# AND A.TPOF_RCPT_YR = #CVLCPT_RCPT_YR# AND A.TPOF_RCPT_NO = #CVLCPT_RCPT_RCPT#