SELECT D1.CVLCPT_CLM_SECD /* 민원청구구분코드 */,
    D1.TPOF_RCPT_NO /* 제보접수번호 */,
    D1.TPOF_RCPT_YR /* 제보접수년도 */,
    D2.TPOF_PRCS_SECD /* 제보처리구분코드 */,
    F_CODE_NM('1000754', D2.TPOF_PRCS_SECD) AS F_CODE_NM,
    NVL(MAX(D3.TPOF_CYCL),1) AS TPOF_CYCL /* 제보차수 */,
    NVL(MIN(D3.TPOF_CYCL),1) AS TPOF_CYCL /* 제보차수 */
FROM TB_CSPDCP101M D1 ,TB_CSPDCP205L D2 ,TB_CSPDCP401H D3 ,TB_CSPDCP402L D4 WHERE 1=1 AND D1.CVLCPT_CLM_SECD = #CLM_SECD# AND D1.TPOF_RCPT_YR = #CVLCPT_RCPT_YR# AND D1.TPOF_RCPT_NO = #CVLCPT_RCPT_RCPT# AND D1.CVLCPT_CLM_SECD = D2.CVLCPT_CLM_SECD AND D1.TPOF_RCPT_YR = D2.TPOF_RCPT_YR AND D1.TPOF_RCPT_NO = D2.TPOF_RCPT_NO AND D2.TPOF_SQNO = #FRS_MDM_SQNO# AND D1.CVLCPT_CLM_SECD = D3.CVLCPT_CLM_SECD(+) AND D1.TPOF_RCPT_YR = D3.TPOF_RCPT_YR(+) AND D1.TPOF_RCPT_NO = D3.TPOF_RCPT_NO(+) AND D1.CVLCPT_CLM_SECD = D4.CVLCPT_CLM_SECD(+) AND D1.TPOF_RCPT_YR = D4.TPOF_RCPT_YR(+) AND D1.TPOF_RCPT_NO = D4.TPOF_RCPT_NO(+) GROUP BY D1.CVLCPT_CLM_SECD ,D1.TPOF_RCPT_NO ,D1.TPOF_RCPT_YR ,D2.TPOF_PRCS_SECD