SELECT C.CLM_SECD /* 청구구분코드 */,
    C.CVLCPT_RCPT_YR /* 민원접수년도 */,
    C.CVLCPT_RCPT_RCPT /* 민원접수번호 */,
    C.CVLCPT_RCPT_YR || '-민원-' || A.TPOF_ACTL_RCPT_NO AS CVLCPT_RCPT_YR /* 민원접수년도 */,
    C.FRS_MDM_SQNO /* 포렌식매체순번 */,
    TO_CHAR(A.TPOF_RCPT_DT, 'YYYY-MM-DD HH24:MM') AS CRU_RCPT_DT /* 부패접수일시 */,
    A.TPOF_NM /* 제보명 */,
    D.TPOF_CN /* 제보내용 */,
    A.TPOF_CVLPR_NM /* 제보민원인명 */,
    F_DPT_INFO(C.TRNF_DEPT_CD, '1') AS F_DPT_INFO,
    F_CODE_NM('1000755' ,E.TPOF_RSLT_RPTP_PRCS_RSLT_SECD) AS F_CODE_NM,
    E.TPOF_RSLT_RPTP_PRCS_RSLT_SECD /* 제보결과보고서처리결과구분코드 */
FROM TB_CSPDCP101M A ,TB_CSPDCP201M B ,(SELECT CLM_SECD /* 청구구분코드 */,
    CVLCPT_RCPT_YR /* 민원접수년도 */,
    CVLCPT_RCPT_RCPT /* 민원접수번호 */,
    TRNF_DEPT_CD /* 이송부서코드 */,
    FRS_MDM_SQNO /* 포렌식매체순번 */
FROM TBCSPDCP205L WHERE 1=1 AND CLM_SECD = '1' AND SLCT_PRCS_PRCS ='70' ) C ,TB_CSPDCP102M D ,TB_CSPDCP402L E WHERE 1=1 AND A.CVLCPT_CLM_SECD = B.CVLCPT_CLM_SECD AND A.TPOF_RCPT_YR = B.TPOF_RCPT_YR AND A.TPOF_RCPT_NO = B.TPOF_RCPT_NO AND A.CVLCPT_CLM_SECD = C.CLM_SECD AND A.TPOF_RCPT_YR = C.CVLCPT_RCPT_YR AND A.TPOF_RCPT_NO = C.CVLCPT_RCPT_RCPT AND A.CVLCPT_CLM_SECD = D.CVLCPT_CLM_SECD AND A.TPOF_RCPT_YR = D.TPOF_RCPT_YR AND A.TPOF_RCPT_NO = D.TPOF_RCPT_NO AND A.CVLCPT_CLM_SECD = '1' AND A.TPOF_STTS_SECD = '60' AND C.CLM_SECD = E.CVLCPT_CLM_SECD(+) AND C.CVLCPT_RCPT_YR = E.TPOF_RCPT_YR(+) AND C.CVLCPT_RCPT_RCPT = E.TPOF_RCPT_NO(+) AND C.FRS_MDM_SQNO = E.TPOF_CYCL(+) AND TO_CHAR(A.TPOF_RCPT_DT,'YYYYMMDD') &gt;= #strACP_DHS# AND TO_CHAR(A.TPOF_RCPT_DT,'YYYYMMDD') &lt;= #strACP_DHE# AND A.TPOF_RCPT_YR = #CVLCPT_RCPT_YR# AND A.TPOF_ACTL_RCPT_NO = #TPOF_ACTL_RCPT_NO# AND C.TRNF_DEPT_CD LIKE SUBSTR(#strHrkDptCd#, 0, 3) || '%' AND C.TRNF_DEPT_CD = #TRNF_DEPT_CD# AND A.TPOF_CVLPR_NM LIKE #strCVPTR_NM# ||'%' AND A.TPOF_NM LIKE '%'||#strTIT_NM#||'%' AND D.TPOF_CN LIKE '%'||#strTIT_NM#||'%' ORDER BY A.TPOF_RCPT_DT DESC