MERGE INTO TB_CSPDCP401H A USING (SELECT #REQ_DVSN_CD# AS CLM_SECD /* 청구구분코드 */,
    #ACP_YR# AS CVLCPT_RCPT_YR /* 민원접수년도 */,
    #CVPT_ACP_NO# AS CVLCPT_RCPT_RCPT /* 민원접수번호 */,
    #DGE# AS TPOF_CYCL /* 제보차수 */
FROM DUAL) B ON (A.CLM_SECD = B.CLM_SECD AND A.CVLCPT_RCPT_YR = B.CVLCPT_RCPT_YR AND A.TPOF_CYCL = B.TPOF_CYCL AND A.CVLCPT_RCPT_RCPT = B.CVLCPT_RCPT_RCPT) WHEN MATCHED THEN UPDATE SET A.ETC_LBSVC_MNG_DEPT_CD = #LAST_PRCS_DEPT_CD# /* 기타용역관리부서코드 */, A.PRCS_PIC_ID = #LAST_USER_ID# /* 처리담당자아이디 */, A.CVLCPT_DOC_ID = #CVLCPT_DOC_ID# /* 민원문서아이디 */, A.TPOF_EXMN_RSLT_UNQ_CN = #TPOF_EXMN_RSLT_UNQ_CN# /* 제보조사결과고유내용 */, A.TPOF_RPLY_YMD = #TPOF_RPLY_YMD# /* 제보회신일자 */, A.LAST_USER_ID = #LAST_USER_ID# /* 최종사용자아이디 */, A.LAST_PRCS_DEPT_CD = #LAST_PRCS_DEPT_CD# /* 최종처리부서코드 */, A.LAST_MENU_ID = #LAST_MENU_ID# /* 최종메뉴아이디 */, A.LAST_MDFCN_DT = SYSDATE WHEN NOT MATCHED THEN INSERT INSERT (CLM_SECD, CVLCPT_RCPT_YR, CVLCPT_RCPT_RCPT, TPOF_CYCL, ETC_LBSVC_MNG_DEPT_CD, PRCS_PIC_ID, TPOF_EXMN_RSLT_UNQ_CN, CVLCPT_DOC_ID, ADT_PLAN_DOC_TPCD, TPOF_RPLY_YMD, FRST_USER_ID, LAST_USER_ID, LAST_PRCS_DEPT_CD, LAST_MENU_ID, FRST_REG_DT, LAST_MDFCN_DT) VALUES (#REQ_DVSN_CD#,#ACP_YR#,#CVPT_ACP_NO#,#DGE#, #LAST_PRCS_DEPT_CD#,#LAST_USER_ID#,#TPOF_EXMN_RSLT_UNQ_CN#,#CVLCPT_DOC_ID#,#DOC_TYP_CD#,#TPOF_RPLY_YMD#, #LAST_USER_ID#,#LAST_USER_ID#,#LAST_PRCS_DEPT_CD#,#LAST_MENU_ID#,SYSDATE,SYSDATE) /* 최종수정일시 */