MERGE INTO TB_CSPDCP402L A USING (SELECT #REQ_DVSN_CD# AS CLM_SECD /* 청구구분코드 */,
    #ACP_YR# AS CVLCPT_RCPT_YR /* 민원접수년도 */,
    #CVPT_ACP_NO# AS CVLCPT_RCPT_RCPT /* 민원접수번호 */,
    #DGE# AS TPOF_CYCL /* 제보차수 */
FROM DUAL) B ON (A.CLM_SECD = B.CLM_SECD AND A.CVLCPT_RCPT_YR = B.CVLCPT_RCPT_YR AND A.TPOF_CYCL = B.TPOF_CYCL AND A.CVLCPT_RCPT_RCPT = B.CVLCPT_RCPT_RCPT) WHEN MATCHED THEN UPDATE SET A.CVLCPT_RSLT_RPTP_PRCS_RSLT_SECD = #CVLCPT_RSLT_RPTP_PRCS_RSLT_SECD# , A.LAST_USER_ID = #LAST_USER_ID# /* 최종사용자아이디 */, A.LAST_PRCS_DEPT_CD = #LAST_PRCS_DEPT_CD# /* 최종처리부서코드 */, A.LAST_MENU_ID = #LAST_MENU_ID# /* 최종메뉴아이디 */, A.LAST_MDFCN_DT = SYSDATE WHEN NOT MATCHED THEN INSERT INSERT (CLM_SECD, CVLCPT_RCPT_YR, CVLCPT_RCPT_RCPT, TPOF_CYCL, CVLCPT_RSLT_RPTP_PRCS_RSLT_SECD, FRST_USER_ID, LAST_USER_ID, LAST_PRCS_DEPT_CD, LAST_MENU_ID, FRST_REG_DT, LAST_MDFCN_DT) VALUES (#REQ_DVSN_CD#,#ACP_YR#,#CVPT_ACP_NO#,#DGE#, #CVLCPT_RSLT_RPTP_PRCS_RSLT_SECD#, #LAST_USER_ID#,#LAST_USER_ID#,#LAST_PRCS_DEPT_CD#,#LAST_MENU_ID#,SYSDATE,SYSDATE) /* 최종수정일시 */