/* 조사결과보고 조회 */ SELECT C.CVLCPT_CLM_SECD /* 민원청구구분코드 */,
    C.TPOF_RCPT_YR /* 제보접수년도 */,
    C.TPOF_RCPT_NO /* 제보접수번호 */,
    NVL(A.TPOF_CYCL,#FRS_MDM_SQNO#) AS TPOF_CYCL /* 제보차수 */,
    NVL(A.TPOF_RPLY_YMD, TO_CHAR(SYSDATE, 'YYYYMMDD')) AS TPOF_RPLY_YMD /* 제보회신일자 */,
    B.TPOF_RSLT_RPTP_PRCS_RSLT_SECD /* 제보결과보고서처리결과구분코드 */,
    A.TPOF_EXMN_RSLT_UNQ_CN /* 제보조사결과고유내용 */,
    A.TPOF_DOC_ID /* 제보문서아이디 */,
    A.TPOF_DOC_TPCD /* 제보문서유형코드 */,
    F_DPT_INFO(C.TPOF_TRNF_DEPT_CD,'1') AS F_DPT_INFO,
    CASE WHEN D.TPOF_ONR_DOC_MNG_CARD_ID IS NOT NULL THEN 'Y' ELSE 'N' END AS TPOF_ONR_DOC_MNG_CARD_ID /* 제보온나라문서관리카드아이디 */,
    CASE WHEN D.TPOF_OPM_ATRZ_STTS_SECD = '30' THEN 'Y' ELSE 'N' END AS TPOF_OPM_ATRZ_STTS_SECD /* 제보시행문결재상태구분코드 */
FROM TB_CSPDCP401H A ,TB_CSPDCP402L B ,TB_CSPDCP205L C ,TB_CSPDCP301M D WHERE 1=1 AND C.CVLCPT_CLM_SECD = A.CVLCPT_CLM_SECD(+) AND C.TPOF_RCPT_YR = A.TPOF_RCPT_YR(+) AND C.TPOF_RCPT_NO = A.TPOF_RCPT_NO(+) AND A.CVLCPT_CLM_SECD = B.CVLCPT_CLM_SECD(+) AND A.TPOF_RCPT_YR = B.TPOF_RCPT_YR(+) AND A.TPOF_RCPT_NO = B.TPOF_RCPT_NO(+) AND A.TPOF_CYCL = B.TPOF_CYCL(+) AND A.TPOF_CYCL(+) = #FRS_MDM_SQNO# AND C.CVLCPT_CLM_SECD = #CLM_SECD# AND C.TPOF_RCPT_YR = #CVLCPT_RCPT_YR# AND C.TPOF_RCPT_NO = #CVLCPT_RCPT_RCPT# AND C.TPOF_SQNO = #FRS_MDM_SQNO# AND A.CVLCPT_CLM_SECD = D.CVLCPT_CLM_SECD(+) AND A.TPOF_RCPT_YR = D.TPOF_RCPT_YR(+) AND A.TPOF_RCPT_NO = D.TPOF_RCPT_NO(+) AND A.TPOF_CYCL = D.TPOF_CYCL(+) AND D.TPOF_AVTSMT_SECD(+) = 'E'