SELECT F_CODE_NM('1000053', D2.TPOF_RSLT_RPTP_PRCS_RSLT_SECD) AS F_CODE_NM,
    F_DPT_INFO(D1.TPOF_PRCS_DEPT_CD, '1') AS PRCS_DEPT_NM /* 처리부서명 */,
    D3.USER_NM /* 사용자명 */,
    TO_CHAR(D1.FRST_REG_DT, 'YYYY-MM-DD') AS FRST_REG_DT /* 최초등록일시 */,
    D1.TPOF_EXMN_RSLT_UNQ_CN /* 제보조사결과고유내용 */,
    D1.TPOF_DOC_ID /* 제보문서아이디 */,
    F_CODE_NM('1000087', D1.TPOF_SMG_CVLCPT_PRCS_SECD) AS F_CODE_NM,
    (CASE WHEN D1.TPOF_OPM_NTFCTN_SECD = 'Y' THEN '통보' ELSE '미통보' END) AS SLCT_OPM_NTFCTN_SECD /* 청탁시행문통보구분코드 */,
    D1.TPOF_BKMK_ANS_CN /* 제보즐겨찾기답변내용 */,
    D1.CVLCPT_CLM_SECD /* 민원청구구분코드 */,
    D1.TPOF_RCPT_YR /* 제보접수년도 */,
    D1.TPOF_RCPT_NO /* 제보접수번호 */,
    D1.TPOF_CYCL /* 제보차수 */,
    (SELECT MIN(FRS_MDM_SQNO) AS FRS_MDM_SQNO /* 포렌식매체순번 */
FROM TB_CSPDCP205L S1 WHERE S1.CLM_SECD = D1.CLM_SECD AND S1.CVLCPT_RCPT_YR = D1.CVLCPT_RCPT_YR AND S1.CVLCPT_RCPT_RCPT = D1.CVLCPT_RCPT_RCPT ) AS FRS_MDM_SQNO FROM TB_CSPDCP401H D1 ,TB_CSPDCP402L D2 ,TBDCMACM001M D3 ,TB_CSPDCP205L F WHERE 1=1 AND D1.CLM_SECD = D2.CVLCPT_CLM_SECD AND D1.CVLCPT_RCPT_YR = D2.TPOF_RCPT_YR AND D1.CVLCPT_RCPT_RCPT = D2.TPOF_RCPT_NO AND D1.TPOF_CYCL = D2.TPOF_CYCL AND D1.PRCS_PIC_ID = D3.USER_ID(+) AND D1.CLM_SECD = #CLM_SECD# AND D1.CVLCPT_RCPT_YR = #CVLCPT_RCPT_YR# AND D1.CVLCPT_RCPT_RCPT = #CVLCPT_RCPT_RCPT# AND D1.CLM_SECD = F.CVLCPT_CLM_SECD AND D1.CVLCPT_RCPT_YR = F.TPOF_RCPT_YR AND D1.CVLCPT_RCPT_RCPT = F.TPOF_RCPT_NO AND ( F.TPOF_SQNO = (SELECT MIN(FRS_MDM_SQNO) AS FRS_MDM_SQNO /* 포렌식매체순번 */
FROM TB_CSPDCP205L A WHERE A.CLM_SECD = #CLM_SECD# AND A.CVLCPT_RCPT_YR = #CVLCPT_RCPT_YR# AND A.CVLCPT_RCPT_RCPT = #CVLCPT_RCPT_RCPT# AND A.SLCT_PRCS_PRCS <> '70') )