SELECT A.TPOF_SMG_CVLCPT_PRCS_SECD /* 제보신문고민원처리구분코드 */,
    F_CODE_NM('1000087', A.TPOF_SMG_CVLCPT_PRCS_SECD) AS F_CODE_NM,
    A.TPOF_BKMK_ANS_CN /* 제보즐겨찾기답변내용 */,
    A.TPOF_DOC_ID /* 제보문서아이디 */,
    (CASE WHEN A.TPOF_OPM_NTFCTN_SECD = 'Y' THEN '통보' ELSE '미통보' END) AS SLCT_OPM_NTFCTN_SECD /* 청탁시행문통보구분코드 */,
    TO_CHAR(C.ONR_ATRZ_CMPTN_DT,'YYYY-MM-DD HH24:MI') AS ONR_ATRZ_CMPTN_DT /* 온나라결재완료일시 */
FROM TB_CSPDCP201M A , TB_CSPDCP301M C WHERE 1=1 AND A.CVLCPT_CLM_SECD = #CLM_SECD# AND A.TPOF_RCPT_YR = #CVLCPT_RCPT_YR# AND A.TPOF_RCPT_NO = #CVLCPT_RCPT_RCPT# AND A.CVLCPT_CLM_SECD = C.CVLCPT_CLM_SECD(+) AND A.TPOF_RCPT_YR = C.TPOF_RCPT_YR(+) AND A.TPOF_RCPT_NO = C.TPOF_RCPT_NO(+) AND C.TPOF_AVTSMT_SECD(+) = 'A'