SELECT D1.TPOF_HMPG_APLY_NO /* 제보누리집신청번호 */,
    D1.TPOF_RCPT_YR /* 제보접수년도 */,
    D1.TPOF_ACTL_RCPT_NO /* 제보실제접수번호 */,
    CASE WHEN D1.TPOF_STTS_SECD = '70' THEN D1.TPOF_STTS_SECD WHEN D2.TPOF_KND_SECD = '1' AND SLCT_PRCS_PRCS IN ('60') THEN '40' WHEN D2.TPOF_KND_SECD = '2' THEN '60' ELSE '60' END AS TPOF_STTS_SECD /* 제보상태구분코드 */,
    F_CODE_NM('1000361',CASE WHEN D1.TPOF_STTS_SECD = '70' THEN D1.TPOF_STTS_SECD WHEN D2.TPOF_KND_SECD = '1' AND SLCT_PRCS_PRCS IN ('60') THEN '40' WHEN D2.TPOF_KND_SECD = '2' THEN '60' ELSE '60' END) AS F_CODE_NM,
    CASE WHEN SLCT_PRCS_PRCS NOT IN ('60') THEN TO_CHAR(D1.LAST_MDFCN_DT, 'YYYYMMDDHH24MISS') ELSE '' END AS SLCT_PRCS_PRCS /* 청탁처리구분코드 */,
    D2.TPOF_BKMK_ANS_CN /* 제보즐겨찾기답변내용 */,
    TO_CHAR(D2.LAST_MDFCN_DT, 'YYYYMMDD') AS TO_CHAR
FROM TB_CSPDCP101M D1 ,TB_CSPDCP201M D2 ,TB_CSPDCP205L D3 WHERE 1=1 AND D1.CVLCPT_CLM_SECD = D2.CVLCPT_CLM_SECD AND D1.TPOF_RCPT_YR = D2.TPOF_RCPT_YR AND D1.TPOF_RCPT_NO = D2.TPOF_RCPT_NO AND D1.CVLCPT_CLM_SECD = #CLM_SECD# AND D1.TPOF_RCPT_YR = #CVLCPT_RCPT_YR# AND D1.TPOF_RCPT_NO = #CVLCPT_RCPT_RCPT# AND D1.CVLCPT_CLM_SECD = D3.CVLCPT_CLM_SECD AND D1.TPOF_RCPT_YR = D3.TPOF_RCPT_YR AND D1.TPOF_RCPT_NO = D3.TPOF_RCPT_NO AND D3.TPOF_SQNO = ( SELECT MIN(Z.TPOF_SQNO) AS TPOF_SQNO /* 제보순번 */
FROM TB_CSPDCP205L Z WHERE Z.CLM_SECD = #CLM_SECD# AND Z.CVLCPT_RCPT_YR = #CVLCPT_RCPT_YR# AND Z.CVLCPT_RCPT_RCPT = #CVLCPT_RCPT_RCPT# )