SELECT A.CVLCPT_CLM_SECD /* 민원청구구분코드 */,
    A.TPOF_RCPT_YR /* 제보접수년도 */,
    A.TPOF_RCPT_NO /* 제보접수번호 */,
    A.TPOF_ACTL_RCPT_NO /* 제보실제접수번호 */,
    A.TPOF_RCPT_YR||'-민원-'||A.TPOF_ACTL_RCPT_NO AS NTFCTN_RCPT_SQNO /* 통보접수순번 */,
    A.TPOF_NM /* 제보명 */,
    DECODE(A.TPOF_CLMT_KDCD,'1',A.TPOF_CVLPR_NM,'2',A.TPOF_CVLPR_RPRSV_NM,'3',A.TPOF_CVLPR_RPRSV_NM,'4',A.TPOF_CVLPR_NM) AS TPOF_CVLPR_NM /* 제보민원인명 */,
    TO_CHAR(A.TPOF_RCPT_DT,'YYYY-MM-DD HH24:MI') AS CRU_RCPT_DT /* 부패접수일시 */,
    CASE WHEN (SELECT COUNT(*)
FROM TB_BADDED030L S1 WHERE A.CVLCPT_CLM_SECD = S1.DSPS_RQT_NM_2 AND A.TPOF_RCPT_YR = S1.DSPS_RQT_NM_1 AND A.TPOF_RCPT_NO = S1.DSPS_RQT_NM_3 ) > 0 THEN 'O' ELSE 'X' END AS AUD_CNT ,CASE WHEN (SELECT COUNT(*)
FROM TB_BADEAR035L S1 WHERE A.CVLCPT_CLM_SECD = S1.DSPS_RQT_NM_2 AND A.TPOF_RCPT_YR = S1.DSPS_RQT_NM_1 AND A.TPOF_RCPT_NO = S1.DSPS_RQT_NM_3 ) > 0 THEN 'O' ELSE 'X' END AS DSP_CNT FROM TB_CSPDCP101M A ,TB_CSPDCP102M B ,TB_CSPDCP201M C WHERE 1=1 AND A.CVLCPT_CLM_SECD = '1' AND A.CVLCPT_CLM_SECD = B.CVLCPT_CLM_SECD AND A.TPOF_RCPT_YR = B.TPOF_RCPT_YR AND A.TPOF_RCPT_NO = B.TPOF_RCPT_NO AND A.CVLCPT_CLM_SECD = C.CVLCPT_CLM_SECD(+) AND A.TPOF_RCPT_YR = C.TPOF_RCPT_YR(+) AND A.TPOF_RCPT_NO = C.TPOF_RCPT_NO(+) AND (A.CVLCPT_CLM_SECD, A.TPOF_RCPT_YR, A.TPOF_RCPT_NO) IN (SELECT DSPS_RQT_NM_2 /* 처분요구명2 */,
    DSPS_RQT_NM_1 /* 처분요구명1 */,
    DSPS_RQT_NM_3 /* 처분요구명3 */
FROM TB_BADDED030L UNION ALL SELECT DSPS_RQT_NM_2 /* 처분요구명2 */,
    DSPS_RQT_NM_1 /* 처분요구명1 */,
    DSPS_RQT_NM_3 /* 처분요구명3 */
FROM TB_BADEAR035L ) AND TO_CHAR(A.TPOF_RCPT_DT,'YYYYMMDD') &gt;= #strACP_DHS# AND TO_CHAR(A.TPOF_RCPT_DT,'YYYYMMDD') &lt;= #strACP_DHE# AND A.TPOF_NM LIKE '%'||#strTIT_NM#||'%' AND A.TPOF_RCPT_YR = #CVLCPT_RCPT_YR# AND A.TPOF_ACTL_RCPT_NO = #TPOF_ACTL_RCPT_NO# AND (A.TPOF_CVLPR_NM LIKE '%'||#strCVPTR_NM#||'%' OR A.TPOF_CVLPR_RPRSV_NM LIKE '%'||#strCVPTR_NM#||'%') ORDER BY A.TPOF_RCPT_YR DESC,A.TPOF_ACTL_RCPT_NO DESC