SELECT T1.CVLCPT_DCLR_SECD /* 민원신고구분코드 */,
    T1.CVLCPT_RCPT_YR /* 민원접수년도 */,
    T1.CVLCPT_DCLR_RCPT_NO /* 민원신고접수번호 */,
    DECODE(T1.SLCT_ACTL_RCPT_NO,NULL,NULL,T1.CVLCPT_RCPT_YR||'-'||'청금-'||T1.SLCT_ACTL_RCPT_NO) AS NTFCTN_RCPT_SQNO /* 통보접수순번 */,
    T1.SLCT_HMPG_APLY_NO /* 청탁누리집신청번호 */,
    T1.SLCT_DCLR_KDCD /* 청탁신고종류코드 */,
    F_CODE_NM('1000758',T1.SLCT_DCLR_KDCD) AS F_CODE_NM,
    T1.SLCT_DCLR_KND_DTL_SECD /* 청탁신고종류상세구분코드 */,
    F_CODE_NM('1000759',T1.SLCT_DCLR_KND_DTL_SECD) AS F_CODE_NM,
    T1.SLCT_RLPR_NM /* 청탁관계자명 */,
    T1.SLCT_TTL /* 청탁제목 */,
    TO_CHAR(T1.SLCT_RCPT_DT,'YYYY-MM-DD HH24:MI') AS CRU_RCPT_DT /* 부패접수일시 */,
    TO_CHAR(T1.FRST_REG_DT,'YYYY-MM-DD HH24:MI') AS FRST_REG_DT /* 최초등록일시 */,
    T1.SLCT_RCPT_PATH_SECD /* 청탁접수경로구분코드 */,
    F_CODE_NM('1000761',T1.SLCT_RCPT_PATH_SECD) AS F_CODE_NM,
    T1.SLCT_PRCS_STTS_SECD /* 청탁처리상태구분코드 */,
    F_CODE_NM('1000760',T1.SLCT_PRCS_STTS_SECD) AS PRCS_STTS_NM /* 처리상태명 */,
    T1.SLCT_ACTL_RCPT_NO /* 청탁실제접수번호 */
FROM TB_CSPDCP811M T1 ,TB_CSPDCP813M T2 WHERE T1.CVLCPT_DCLR_SECD = '1' AND T1.SLCT_PRCS_STTS_SECD NOT IN ('1') AND T1.CVLCPT_DCLR_SECD = T2.CVLCPT_DCLR_SECD(+) AND T1.CVLCPT_RCPT_YR = T2.CVLCPT_RCPT_YR(+) AND T1.CVLCPT_DCLR_RCPT_NO = T2.CVLCPT_DCLR_RCPT_NO(+) AND (T1.SLCT_RCPT_DEPT_CD = #ETC_LBSVC_MNG_DEPT_CD# OR T2.SLCT_PRCS_DEPT_CD = #ETC_LBSVC_MNG_DEPT_CD#) AND T1.CVLCPT_RCPT_YR = #CVLCPT_RCPT_YR# AND T1.SLCT_ACTL_RCPT_NO = #CRU_ACTL_RCPT_NO# AND TO_CHAR(T1.SLCT_RCPT_DT,'YYYYMMDD') &gt;= #strACP_DHS# AND TO_CHAR(T1.SLCT_RCPT_DT,'YYYYMMDD') &lt;= #strACP_DHE# AND T1.SLCT_PRCS_STTS_SECD = #CRU_PRCS_STTS_SECD# AND T1.SLCT_TTL LIKE '%'||#strTIT_NM#||'%' AND T1.SLCT_RLPR_NM LIKE '%'||#strNAM#||'%' AND ( (T2.SLCT_PRCS_DEPT_CD IS NOT NULL AND T2.SLCT_PRCS_DEPT_CD <> #ETC_LBSVC_MNG_DEPT_CD#) OR (T2.SLCT_PRCS_DEPT_CD = #ETC_LBSVC_MNG_DEPT_CD# AND T2.SLCT_PRCS_PIC_ID IS NOT NULL) OR (T1.SLCT_PRCS_STTS_SECD IN ('9'))) AND (T2.SLCT_PRCS_DEPT_CD IS NULL OR (T2.SLCT_PRCS_DEPT_CD = #ETC_LBSVC_MNG_DEPT_CD# AND T2.SLCT_PRCS_PIC_ID IS NULL)) AND T1.SLCT_PRCS_STTS_SECD NOT IN('9','10') ORDER BY T1.CVLCPT_RCPT_YR DESC,TO_NUMBER(T1.SLCT_ACTL_RCPT_NO) DESC