MERGE INTO TB_CSPDCP813M A USING ( SELECT #STTM_DVSN_CD# AS CVLCPT_DCLR_SECD /* 민원신고구분코드 */,
    #ACP_YR# AS CVLCPT_RCPT_YR /* 민원접수년도 */,
    #STTM_ACP_NO# AS CVLCPT_DCLR_RCPT_NO /* 민원신고접수번호 */
FROM DUAL ) B ON ( A.CVLCPT_DCLR_SECD = B.CVLCPT_DCLR_SECD AND A.CVLCPT_RCPT_YR = B.CVLCPT_RCPT_YR AND A.CVLCPT_DCLR_RCPT_NO = B.CVLCPT_DCLR_RCPT_NO ) WHEN MATCHED THEN UPDATE SET SLCT_LAST_HNDT_DEPT_CD = #LAST_PRCS_DEPT_CD# /* 청탁최종배부부서코드 */, SLCT_LAST_HNDT_PIC_ID = #LAST_USER_ID# /* 청탁최종배부담당자아이디 */, CRU_LAST_HNDT_DT = SYSDATE /* 부패최종배부일시 */, ETC_LBSVC_MNG_DEPT_CD = #ETC_LBSVC_MNG_DEPT_CD# /* 기타용역관리부서코드 */, PRCS_PIC_ID = #PRCS_PIC_ID# /* 처리담당자아이디 */, LAST_USER_ID = #LAST_USER_ID# /* 최종사용자아이디 */, LAST_PRCS_DEPT_CD = #LAST_PRCS_DEPT_CD# /* 최종처리부서코드 */, LAST_MENU_ID = #LAST_MENU_ID# /* 최종메뉴아이디 */, LAST_MDFCN_DT = SYSDATE WHEN NOT MATCHED THEN INSERT INSERT ( CVLCPT_DCLR_SECD ,CVLCPT_RCPT_YR ,CVLCPT_DCLR_RCPT_NO ,SLCT_LAST_HNDT_DEPT_CD ,SLCT_LAST_HNDT_PIC_ID ,CRU_LAST_HNDT_DT ,ETC_LBSVC_MNG_DEPT_CD ,PRCS_PIC_ID ,FRST_USER_ID ,LAST_USER_ID ,LAST_PRCS_DEPT_CD ,LAST_MENU_ID ,FRST_REG_DT ,LAST_MDFCN_DT ) VALUES ( #STTM_DVSN_CD# ,#ACP_YR# ,#STTM_ACP_NO# ,#LAST_PRCS_DEPT_CD# ,#LAST_USER_ID# ,SYSDATE ,#ETC_LBSVC_MNG_DEPT_CD# ,#PRCS_PIC_ID# ,#LAST_USER_ID# ,#LAST_USER_ID# ,#LAST_PRCS_DEPT_CD# ,#LAST_MENU_ID# ,SYSDATE ,SYSDATE ) /* 최종수정일시 */