SELECT T1.CVLCPT_DCLR_SECD /* 민원신고구분코드 */,
    T1.CVLCPT_RCPT_YR /* 민원접수년도 */,
    T1.CVLCPT_DCLR_RCPT_NO /* 민원신고접수번호 */,
    DECODE(T1.SLCT_ACTL_RCPT_NO,NULL,NULL,T1.CVLCPT_RCPT_YR||'-'||'청금-'||T1.SLCT_ACTL_RCPT_NO) AS NTFCTN_RCPT_SQNO /* 통보접수순번 */,
    T1.SLCT_HMPG_APLY_NO /* 청탁누리집신청번호 */,
    T1.SLCT_DCLR_KDCD /* 청탁신고종류코드 */,
    F_CODE_NM('1000758',T1.SLCT_DCLR_KDCD) AS F_CODE_NM,
    T1.SLCT_DCLR_KND_DTL_SECD /* 청탁신고종류상세구분코드 */,
    F_CODE_NM('1000759',T1.SLCT_DCLR_KND_DTL_SECD) AS F_CODE_NM,
    T1.SLCT_RLPR_NM /* 청탁관계자명 */,
    CASE WHEN ( SELECT COUNT(*)
FROM TB_CSPDCP819L S1 WHERE S1.CVLCPT_DCLR_SECD = T1.CVLCPT_DCLR_SECD AND S1.CVLCPT_RCPT_YR = T1.CVLCPT_RCPT_YR AND S1.CVLCPT_DCLR_RCPT_NO = T1.CVLCPT_DCLR_RCPT_NO ) > 0 THEN T1.SLCT_TTL||'(합건 : '|| ( SELECT AGGR_CONCAT(S3.CVLCPT_RCPT_YR||'-청금-'||S3.SLCT_ACTL_RCPT_NO, ', ')||' )' AS CVLCPT_RCPT_YR /* 민원접수년도 */
FROM TB_CSPDCP819L S2, TB_CSPDCP811M S3 WHERE T1.CVLCPT_DCLR_SECD = S2.CVLCPT_DCLR_SECD AND T1.CVLCPT_RCPT_YR = S2.CVLCPT_RCPT_YR AND T1.CVLCPT_DCLR_RCPT_NO = S2.CVLCPT_DCLR_RCPT_NO AND S2.CVLCPT_MCA_DCLR_SECD = S3.CVLCPT_DCLR_SECD AND S2.CVLCPT_MCA_RCPT_YR = S3.CVLCPT_RCPT_YR AND S2.CVLCPT_MCA_DCLR_RCPT_NO = S3.CVLCPT_DCLR_RCPT_NO) ELSE T1.SLCT_TTL END AS RSCH_ASMT_TTL ,TO_CHAR(T1.SLCT_RCPT_DT,'YYYY-MM-DD HH24:MI') AS CRU_RCPT_DT ,TO_CHAR(T1.FRST_REG_DT,'YYYY-MM-DD HH24:MI') AS FRST_REG_DT ,T1.SLCT_RCPT_PATH_SECD ,F_CODE_NM('1000761',T1.SLCT_RCPT_PATH_SECD) AS ACP_RUT_NM ,T1.SLCT_PRCS_STTS_SECD ,F_CODE_NM('1000760',T1.SLCT_PRCS_STTS_SECD) AS PRCS_STTS_NM ,T1.SLCT_ACTL_RCPT_NO ,TO_CHAR(T2.TPOF_LAST_PRCS_DT,'YYYY-MM-DD HH24:MI') AS TPOF_LAST_PRCS_DT ,TO_CHAR(TO_DATE(F_GET_NEXT_WORK_DT(TO_CHAR(T1.SLCT_RCPT_DT, 'YYYYMMDD'),31)),'YYYY-MM-DD') AS HNDL_LIMIT__DT ,TO_CHAR(T1.SLCT_DEL_DT,'YYYY-MM-DD HH24:MI') AS CRU_DEL_DT FROM TB_CSPDCP811M T1 ,TB_CSPDCP813M T2 WHERE T1.CVLCPT_DCLR_SECD = '1' AND T1.CVLCPT_DCLR_SECD = T2.CVLCPT_DCLR_SECD AND T1.CVLCPT_RCPT_YR = T2.CVLCPT_RCPT_YR AND T1.CVLCPT_DCLR_RCPT_NO = T2.CVLCPT_DCLR_RCPT_NO AND (T1.CVLCPT_DCLR_SECD, T1.CVLCPT_RCPT_YR, T1.CVLCPT_DCLR_RCPT_NO) NOT IN ( SELECT CVLCPT_MCA_DCLR_SECD /* 민원합건신고구분코드 */,
    TPOF_MCA_RCPT_YR /* 제보합건접수년도 */,
    CVLCPT_MCA_DCLR_RCPT_NO /* 민원합건신고접수번호 */
FROM TB_CSPDCP819L ) AND T2.ETC_LBSVC_MNG_DEPT_CD = #ETC_LBSVC_MNG_DEPT_CD# AND T2.PRCS_PIC_ID = #PRCS_PIC_ID# AND T1.CVLCPT_RCPT_YR = #CVLCPT_RCPT_YR# AND T1.SLCT_ACTL_RCPT_NO = #CRU_ACTL_RCPT_NO# AND TO_CHAR(T1.SLCT_RCPT_DT,'YYYYMMDD') &gt;= #strACP_DHS# AND TO_CHAR(T1.SLCT_RCPT_DT,'YYYYMMDD') &lt;= #strACP_DHE# AND T1.SLCT_DCLR_KDCD = #SLCT_DCLR_KDCD# AND T1.SLCT_DCLR_KND_DTL_SECD = #CRU_DCLR_KND_DTL_SECD# AND T1.SLCT_PRCS_STTS_SECD = #CRU_PRCS_STTS_SECD# AND T1.SLCT_TTL LIKE '%'||#strTIT_NM#||'%' AND T1.SLCT_RLPR_NM LIKE '%'||#strNAM#||'%' AND T1.SLCT_PRCS_STTS_SECD IN ('9','10') AND T1.SLCT_PRCS_STTS_SECD NOT IN ('9','10') ORDER BY T1.CVLCPT_RCPT_YR DESC,TO_NUMBER(T1.SLCT_ACTL_RCPT_NO) DESC