SELECT T1.CVLCPT_DCLR_SECD /* 민원신고구분코드 */,
    T1.CVLCPT_RCPT_YR /* 민원접수년도 */,
    T1.CVLCPT_DCLR_RCPT_NO /* 민원신고접수번호 */,
    T1.SLCT_TELNO /* 청탁전화번호 */
FROM TB_CSPDCP811M T1, TB_CSPDCP819L T2 WHERE T1.CVLCPT_DCLR_SECD = T2.CVLCPT_MCA_DCLR_SECD AND T1.CVLCPT_RCPT_YR = T2.CVLCPT_MCA_RCPT_YR AND T1.CVLCPT_DCLR_RCPT_NO = T2.CVLCPT_MCA_DCLR_RCPT_NO AND T2.CVLCPT_DCLR_SECD = #CVLCPT_DCLR_SECD# AND T2.CVLCPT_RCPT_YR = #CVLCPT_RCPT_YR# AND T2.CVLCPT_DCLR_RCPT_NO = #CVLCPT_DCLR_RCPT_NO# AND T1.SLCT_TELNO IS NOT NULL