SELECT A.CVLCPT_CLM_SECD /* 민원청구구분코드 */,
    A.TPOF_RCPT_YR /* 제보접수년도 */,
    A.TPOF_RCPT_NO /* 제보접수번호 */,
    A.TPOF_RCPT_YR||'-'||F_CODE_NM('1000740',A.CVLCPT_CLM_SECD)||'-'||A.TPOF_RCPT_NO AS NTFCTN_RCPT_SQNO /* 통보접수순번 */,
    DECODE(A.TPOF_CLMT_KDCD,'1',A.TPOF_CVLPR_NM,'2',A.TPOF_CVLPR_RPRSV_NM,'3',A.TPOF_CVLPR_RPRSV_NM,'4',A.TPOF_CVLPR_NM) AS TPOF_CVLPR_NM /* 제보민원인명 */,
    A.TPOF_NM /* 제보명 */,
    A.TPOF_RCPT_DT /* 제보접수일시 */,
    B.CLMT_KDCD /* 청구인종류코드 */,
    A.TPOF_STTS_SECD /* 제보상태구분코드 */,
    ( SELECT CASE WHEN COUNT(*) > 1 THEN F_ORG_INFO(MIN(CVLCPT_ADT_TRGT_INST_CD),'1')||'외 '|| TO_CHAR(COUNT(*)-1)||'건' ELSE F_ORG_INFO(MIN(CVLCPT_ADT_TRGT_INST_CD),'1') AS 1
FROM TB_CSPDCP104L Z WHERE A.CLM_SECD = Z.CVLCPT_CLM_SECD AND A.CVLCPT_RCPT_YR = Z.TPOF_RCPT_YR AND A.CVLCPT_RCPT_RCPT = Z.TPOF_RCPT_NO ) AS AUD_TG_ORG_NM FROM TB_CSPDCP101M A ,TB_CSPEAR001M B WHERE 1=1 AND A.CLM_SECD = B.CLM_SECD(+) AND A.CVLCPT_RCPT_YR = B.CLM_YR(+) AND A.CVLCPT_RCPT_RCPT = B.CLM_RCPT_NO(+) AND A.CLM_SECD = #CLM_SECD# AND A.CLM_SECD IN ('2','3') AND A.CRU_RCPT_DT &gt;= #strACP_DHS# AND A.CRU_RCPT_DT &lt;= #strACP_DHE# AND A.RSCH_ASMT_TTL LIKE '%'||#strTIT_NM#||'%' AND A.CVLCPT_RCPT_YR = #CVLCPT_RCPT_YR# AND A.CVLCPT_RCPT_RCPT = #CVLCPT_RCPT_RCPT# AND A.CRU_PRCS_PRCS_STTS = #CRU_PRCS_PRCS_STTS# AND (A.TPOF_CVLPR_NM LIKE '%'||#strCVPTR_NM#||'%' OR A.SLCT_CVLPR_RPRSV_NM LIKE '%'||#strCVPTR_NM#||'%') ORDER BY A.CVLCPT_RCPT_YR DESC, A.CVLCPT_RCPT_RCPT DESC