SELECT A.CVLCPT_CLM_SECD /* 민원청구구분코드 */,
    A.TPOF_RCPT_YR /* 제보접수년도 */,
    A.TPOF_RCPT_NO /* 제보접수번호 */,
    A.TPOF_RCPT_YR||'-'||F_CODE_NM('1000740',A.CVLCPT_CLM_SECD)||'-'||A.TPOF_RCPT_NO AS NTFCTN_RCPT_SQNO /* 통보접수순번 */,
    A.TPOF_CVLPR_NM /* 제보민원인명 */,
    A.TPOF_NM /* 제보명 */,
    A.TPOF_RCPT_DT /* 제보접수일시 */,
    B.CLMT_KDCD /* 청구인종류코드 */,
    A.TPOF_STTS_SECD /* 제보상태구분코드 */,
    F_USR_INFO_BY_ID(B.PRCS_PIC_ID,'2') AS SRECS_PRCS_PIC_NM /* 심사재심의처리담당자명 */,
    F_DPT_INFO(B.PRCS_DEPT_CD,'1') AS PRCS_DEPT_NM /* 처리부서명 */,
    B.PRCS_PIC_ID /* 처리담당자아이디 */,
    B.PRCS_DEPT_CD /* 처리부서코드 */,
    CASE WHEN A.CVLCPT_CLM_SECD IN ('2','3') AND RVW_RPT_ATRZ_YMD IS NULL THEN CASE WHEN A.CVLCPT_CLM_SECD = '2' THEN TO_CHAR(A.TPOF_RCPT_DT+30+F_GET_DAY_CNT(A.CVLCPT_CLM_SECD,A.TPOF_RCPT_YR,A.TPOF_RCPT_NO),'YYYYMMDD') ELSE TO_CHAR(ADD_MONTHS(A.TPOF_RCPT_DT ,1)+F_GET_DAY_CNT(A.CVLCPT_CLM_SECD,A.TPOF_RCPT_YR,A.TPOF_RCPT_NO),'YYYYMMDD') END WHEN A.CVLCPT_CLM_SECD = '2' THEN TO_CHAR(TO_DATE(RVW_RPT_ATRZ_YMD)+60, 'YYYYMMDD') ELSE TO_CHAR(ADD_MONTHS(TO_DATE(RVW_RPT_ATRZ_YMD) , 6), 'YYYYMMDD') END AS PRCS_TERM_YMD /* 처리기한일자 */,
    CASE WHEN A.TPOF_STTS_SECD = '170' THEN NULL WHEN SYSDATE - ADD_MONTHS(A.TPOF_RCPT_DT,3) > 0 THEN ROUND(SYSDATE - ADD_MONTHS(A.TPOF_RCPT_DT,3)) ELSE NULL END AS TPOF_STTS_SECD /* 제보상태구분코드 */
FROM TB_CSPDCP101M A ,TB_CSPEAR001M B WHERE 1=1 AND A.CVLCPT_CLM_SECD = B.CLM_SECD(+) AND A.TPOF_RCPT_YR = B.CLM_YR(+) AND A.TPOF_RCPT_NO = B.CLM_RCPT_NO(+) AND A.TPOF_STTS_SECD NOT IN ('10','20','21') AND (#strAuth# = 'ALL' OR B.PRCS_DEPT_CD = #ETC_LBSVC_MNG_DEPT_CD#) AND A.CVLCPT_CLM_SECD = #CLM_SECD# AND A.CVLCPT_CLM_SECD IN ('2','3') AND TO_CHAR(A.TPOF_RCPT_DT,'YYYYMMDD') &gt;= #strACP_DHS# AND TO_CHAR(A.TPOF_RCPT_DT,'YYYYMMDD') &lt;= #strACP_DHE# AND A.TPOF_NM LIKE '%'||#strTIT_NM#||'%' AND A.TPOF_RCPT_YR = #CVLCPT_RCPT_YR# AND A.TPOF_RCPT_NO = #CVLCPT_RCPT_RCPT# AND A.TPOF_STTS_SECD = #CRU_PRCS_PRCS_STTS# AND (A.TPOF_CVLPR_NM LIKE '%'||#strCVPTR_NM#||'%' OR A.TPOF_CVLPR_RPRSV_NM LIKE '%'||#strCVPTR_NM#||'%') ORDER BY A.TPOF_RCPT_YR DESC, A.TPOF_RCPT_NO DESC