SELECT NVL(A.CMT_HDMT_YN,'N') AS CMT_HDMT_YN /* 위원회개최여부 */,
    DECODE(A.CMT_HDMT_YN,'Y', A.CLM_SECD) AS CMT_HDMT_YN /* 위원회개최여부 */,
    A.CMT_HDMT_YR /* 위원회개최년도 */,
    A.CMT_CYCL /* 위원회차수 */,
    A.CMT_HDMT_YMD /* 위원회개최일자 */,
    A.RVW_RSLT_SECD /* 검토결과구분코드 */,
    A.CMT_LPRVS_TPCD /* 위원회조문유형코드 */,
    A.OCRN_TPCD /* 발생유형코드 */,
    A.RVW_RPT_ATRZ_YMD /* 검토보고결재일자 */,
    A.RVW_RSLT_RPLY_YMD /* 검토결과회신일자 */,
    A.FRWD_YMD /* 이첩일자 */,
    A.FRWD_INST_CD /* 이첩기관코드 */,
    ( SELECT Z.USR_DFN_TXT_1
FROM TBDCMACM013C Z WHERE Z.CMM_CD_DVSN_CD ='1000128' AND Z.CD = D.SLCT_PBNST_SECD) AS TON_DVSN_CD ,F_ORG_HRK_NM(D.SLCT_JRSD_SG_SECD) AS COPT_SG_NM ,D.PROF_RBPRSN_INST_NM AS TON_ORG_NM ,A.#N/A ,A.#N/A ,A.#N/A ,TO_CHAR(B.TPOF_DISCON_DT,'YYYYMMDD') AS TPOF_DISCON_DT ,DECODE(B.TPOF_DISCON_DT,NULL,'','취하') AS CVPT_WDW_NM ,A.CLM_SECD ,A.CLM_YR ,A.CLM_RCPT_NO ,B.TPOF_STTS_SECD ,( SELECT CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TB_CSPEAR004M Z WHERE A.CLM_SECD = Z.CLM_SECD AND A.CLM_YR = Z.CVLCPT_RCPT_YR AND A.CLM_RCPT_NO = Z.CVLCPT_RCPT_RCPT AND Z.CLM_DOC_KDCD = '2') AS EXAM_DOC_ID ,C.ONR_DOC_MNG_CARD_ID ,NVL(CLM_DOC_KDCD,'2') AS CLM_DOC_KDCD ,A.RVW_RPT_NTFCTN_YN ,B.TPOF_NM ,C.CVLCPT_ATRZ_PRCS_STTS_SECD ,A.ACADT_DCSN_TERM_EXCL_SECD ,A.CMT_RSLT_TPCD ,A.CMT_DCSN_CNSTN_YMD ,NVL(A.ADT_REF_DATA_YN,'N') AS ADT_REF_DATA_YN ,A.CMT_JRSD_DEPT_CD ,F_DPT_INFO(A.CMT_JRSD_DEPT_CD,'1') AS CMT_COPT_DPT_NM ,A.CMT_SNDNG_YMD ,A.CMT_RB_NM ,A.CMT_DOC_NO FROM TB_CSPEAR001M A ,TB_CSPDCP101M B ,TBDCMACM015M D ,TB_CSPEAR002M C WHERE A.CLM_SECD = #CLM_SECD# AND A.CLM_YR = #CVLCPT_RCPT_YR# AND A.CLM_RCPT_NO = #CVLCPT_RCPT_RCPT# AND A.CLM_SECD = B.CVLCPT_CLM_SECD AND A.CLM_YR = B.TPOF_RCPT_YR AND A.CLM_RCPT_NO = B.TPOF_RCPT_NO AND A.FRWD_INST_CD = D.INST_CD(+) AND A.CLM_SECD = C.CLM_SECD(+) AND A.CLM_YR = C.CLM_YR(+) AND A.CLM_RCPT_NO = C.CLM_RCPT_NO(+) AND C.CLM_DOC_KDCD(+) = '2'