MERGE INTO TB_CSPEAR009M D1 USING ( SELECT #REQ_DVSN_CD# AS CLM_SECD /* 청구구분코드 */,
    #ACP_YR# AS CVLCPT_RCPT_YR /* 민원접수년도 */,
    #CVPT_ACP_NO# AS CVLCPT_RCPT_RCPT /* 민원접수번호 */,
    #APV_DOC_DVSN_CD# AS CRU_ATRZ_DOC_SECD /* 부패결재문서구분코드 */
FROM DUAL ) D2 ON ( D1.CLM_SECD = D2.CLM_SECD AND D1.CLM_YR = D2.CVLCPT_RCPT_YR AND D1.CLM_RCPT_NO = D2.CVLCPT_RCPT_RCPT AND D1.ATRZ_DOC_SECD = D2.CRU_ATRZ_DOC_SECD ) WHEN MATCHED THEN UPDATE SET APPR_SECD = #APPR_SECD# /* 결재권자구분코드 */, DVCF_ATRZ_YMD = #DVCF_ATRZ_YMD# /* 과장결재일자 */, LDER_ATRZ_YMD = #LDER_ATRZ_YMD# /* 단장결재일자 */, HEDV_ATRZ_YMD = #HEDV_ATRZ_YMD# /* 본부장결재일자 */, OW_PSD_ATRZ_YMD = #OW_PSD_ATRZ_YMD# /* 사무총장결재일자 */, GNLGR_ATRZ_YMD = #GNLGR_ATRZ_YMD# /* 원장결재일자 */, LAST_USER_ID = #LAST_USER_ID# /* 최종사용자아이디 */, LAST_PRCS_DEPT_CD = #LAST_PRCS_DEPT_CD# /* 최종처리부서코드 */, LAST_MENU_ID = #LAST_MENU_ID# /* 최종메뉴아이디 */, LAST_MDFCN_DT = SYSDATE WHEN NOT MATCHED THEN INSERT INSERT ( CLM_SECD , CVLCPT_RCPT_YR , CVLCPT_RCPT_RCPT , CRU_ATRZ_DOC_SECD , APPR_SECD , DVCF_ATRZ_YMD , LDER_ATRZ_YMD , HEDV_ATRZ_YMD , OW_PSD_ATRZ_YMD , GNLGR_ATRZ_YMD , FRST_USER_ID , LAST_USER_ID , LAST_PRCS_DEPT_CD , LAST_MENU_ID , FRST_REG_DT , LAST_MDFCN_DT ) VALUES ( #REQ_DVSN_CD# , #ACP_YR# , #CVPT_ACP_NO# , #APV_DOC_DVSN_CD# , #APPR_SECD# , #DVCF_ATRZ_YMD# , #LDER_ATRZ_YMD# , #HEDV_ATRZ_YMD# , #OW_PSD_ATRZ_YMD# , #GNLGR_ATRZ_YMD# , #LAST_USER_ID# , #LAST_USER_ID# , #LAST_PRCS_DEPT_CD# , #LAST_MENU_ID# , SYSDATE , SYSDATE ) /* 최종수정일시 */