    ( SELECT Z.DOC_ID AS CVLCPT_DOC_ID /* 문서아이디 */
FROM TB_CSPEAR004M Z, ECM_FILE_LIST_VIEW2 X WHERE A.CVLCPT_CLM_SECD = Z.CLM_SECD AND A.TPOF_RCPT_YR = Z.CLM_YR AND A.TPOF_RCPT_NO = Z.CLM_RCPT_NO AND Z.DOC_ID = X.CVLCPT_DOC_ID AND Z.CLM_DOC_KDCD = '1' GROUP BY Z.DOC_ID) AS PREI_DOC_ID ,DECODE(( SELECT Z.DOC_ID /* 문서아이디 */
FROM TB_CSPEAR004M Z, ECM_FILE_LIST_VIEW2 X WHERE A.CVLCPT_CLM_SECD = Z.CLM_SECD AND A.TPOF_RCPT_YR = Z.CLM_YR AND A.TPOF_RCPT_NO = Z.CLM_RCPT_NO AND Z.DOC_ID = X.CVLCPT_DOC_ID AND Z.CLM_DOC_KDCD = '1' GROUP BY Z.DOC_ID),NULL,NULL,'파일다운') AS PREI_DOC_DOWN ,( SELECT Z.DOC_ID /* 문서아이디 */
FROM TB_CSPEAR004M Z, ECM_FILE_LIST_VIEW2 X WHERE A.CVLCPT_CLM_SECD = Z.CLM_SECD AND A.TPOF_RCPT_YR = Z.CLM_YR AND A.TPOF_RCPT_NO = Z.CLM_RCPT_NO AND Z.DOC_ID = X.CVLCPT_DOC_ID AND Z.CLM_DOC_KDCD = '2' GROUP BY Z.DOC_ID) AS EXAM_DOC_ID ,DECODE(( SELECT Z.DOC_ID /* 문서아이디 */
FROM TB_CSPEAR004M Z, ECM_FILE_LIST_VIEW2 X WHERE A.CVLCPT_CLM_SECD = Z.CLM_SECD AND A.TPOF_RCPT_YR = Z.CLM_YR AND A.TPOF_RCPT_NO = Z.CLM_RCPT_NO AND Z.DOC_ID = X.CVLCPT_DOC_ID AND Z.CLM_DOC_KDCD = '2' GROUP BY Z.DOC_ID),NULL,NULL,'파일다운') AS EXAM_DOC_DOWN ,( SELECT Z.DOC_ID /* 문서아이디 */
FROM TB_BADDED021M Z, ECM_FILE_LIST_VIEW2 X WHERE C.ADT_YR = Z.CVLCPT_ADT_YR AND C.ADT_NO = Z.ADT_NO AND Z.DOC_ID = X.CVLCPT_DOC_ID AND Z.DOC_TPCD = 'AD-AR-003' GROUP BY Z.DOC_ID) AS RTN_DOC_ID ,DECODE(( SELECT Z.DOC_ID /* 문서아이디 */
FROM TB_BADDED021M Z, ECM_FILE_LIST_VIEW2 X WHERE C.ADT_YR = Z.CVLCPT_ADT_YR AND C.ADT_NO = Z.ADT_NO AND Z.DOC_ID = X.CVLCPT_DOC_ID AND Z.DOC_TPCD = 'AD-AR-003' GROUP BY Z.DOC_ID),NULL,NULL,'파일다운') AS RTN_DOC_DOWN ,( SELECT Z.DOC_ID /* 문서아이디 */
FROM TB_BADDED021M Z, ECM_FILE_LIST_VIEW2 X WHERE C.ADT_YR = Z.CVLCPT_ADT_YR AND C.ADT_NO = Z.ADT_NO AND Z.DOC_ID = X.CVLCPT_DOC_ID AND Z.DOC_TPCD = 'AD-AR-001' GROUP BY Z.DOC_ID) AS RESULT_DOC_ID ,DECODE(( SELECT Z.DOC_ID /* 문서아이디 */
FROM TB_BADDED021M Z, ECM_FILE_LIST_VIEW2 X WHERE C.ADT_YR = Z.CVLCPT_ADT_YR AND C.ADT_NO = Z.ADT_NO AND Z.DOC_ID = X.CVLCPT_DOC_ID AND Z.DOC_TPCD = 'AD-AR-001' GROUP BY Z.DOC_ID),NULL,NULL,'파일다운') AS RESULT_DOC_DOWN FROM TB_CSPDCP101M A ,TB_CSPEAR001M B ,TB_BADDED001M C WHERE 1=1 AND A.CVLCPT_CLM_SECD = B.CLM_SECD(+) AND A.TPOF_RCPT_YR = B.CVLCPT_RCPT_YR(+) AND A.TPOF_RCPT_NO = B.CVLCPT_RCPT_RCPT(+) AND A.CVLCPT_CLM_SECD = C.CLM_SECD(+) AND A.TPOF_RCPT_YR = C.INFO_RCPT_YR(+) AND A.TPOF_RCPT_NO = C.CVLCPT_NO(+) AND A.TPOF_STTS_SECD NOT IN ('21') AND A.CVLCPT_CLM_SECD = #CLM_SECD# AND A.CVLCPT_CLM_SECD IN ('2','3') AND TO_CHAR(A.TPOF_RCPT_DT,'YYYYMMDD') &gt;= #strACP_DHS# AND TO_CHAR(A.TPOF_RCPT_DT,'YYYYMMDD') &lt;= #strACP_DHE# AND A.TPOF_NM LIKE '%'||#strTIT_NM#||'%' AND A.TPOF_RCPT_YR = #CVLCPT_RCPT_YR# AND A.TPOF_RCPT_NO = #CVLCPT_RCPT_RCPT# AND B.CLMT_KDCD = #CLMT_KDCD# AND A.TPOF_STTS_SECD = #CRU_PRCS_PRCS_STTS# AND B.ETC_LBSVC_MNG_DEPT_CD IN (SELECT SRECS_DEPT_CD /* 심사재심의부서코드 */
