MERGE INTO TB_CSPEAR001M A USING ( SELECT #REQ_DVSN_CD# AS CLM_SECD /* 청구구분코드 */,
    #ACP_YR# AS CVLCPT_RCPT_YR /* 민원접수년도 */,
    #CVPT_ACP_NO# AS CVLCPT_RCPT_RCPT /* 민원접수번호 */
