SELECT D2.QSTNAI_YR||'-'||D2.QSTNAI_NO||'-'||D2.DTIL_ANS_NO||'-'||D2.DTIL_DVSN_NO AS QSTNAI_YR /* 세부질의번호 */,
    D2.QSTNAI_YR AS QSTNAI_YR /* 질의서년도 */,
    D2.QSTNAI_NO AS QSTNAI_NO /* 질의서번호 */,
    D2.DTIL_ANS_NO AS DTIL_ANS_NO /* 세부답변번호 */,
    D2.DTIL_DVSN_NO AS DTIL_DVSN_NO /* 세부구분번호 */,
    D2.PIC_NM AS PIC_NM /* 담당자명 */,
    D2.PIC_ID AS PIC_ID /* 담당자아이디 */,
    D2.TKCG_DEPT_NM AS TKCG_DEPT_NM /* 담당부서명 */,
    D2.DTIL_QUE_TIT_NM AS DTIL_QUE_TIT_NM /* 세부질의제목 */,
    D2.QUE_TXT AS QUE_TXT /* 질의내용 */,
    D2.ANS_MTRL_TXT /* 답변내용 */,
    D2.SBMSN_YMD AS SBMSN_YMD /* 제출일자 */,
    '답변자료' AS HWP_BUT /* 답변자료 버튼 명 */,
    F_CODE_NM ('1000374',decode(D2.NTAS_RQ_MTRL_HNDL_STA_CD, '20', '30', D2.NTAS_RQ_MTRL_HNDL_STA_CD)) AS F_CODE_NM /* 처리상태코드 */,
    ( SELECT Z1.SRECS_SRNG_MBCMT_NM AS SRECS_SRNG_MBCMT_NM /* 심사재심의심사위원명 */
FROM TBCSPFPL001M Z1 WHERE Z1.QSTNAI_YR=D2.QSTNAI_YR and Z1.QSTNAI_NO=D2.QSTNAI_NO) AS SRECS_SRNG_MBCMT_NM /*위원명*/, ( SELECT Z1.RQT_YMD /* 요구일자 */
FROM TBCSPFPL001M Z1 WHERE Z1.QSTNAI_YR=D2.QSTNAI_YR and Z1.QSTNAI_NO=D2.QSTNAI_NO) AS RQT_YMD /*요청일자*/, ( SELECT Z1.SMT_RQ_DT
FROM TBCSPFPL001M Z1 WHERE Z1.QSTNAI_YR=D2.QSTNAI_YR and Z1.QSTNAI_NO=D2.QSTNAI_NO) AS SMT_RQ_DT /*제출일자*/, ( SELECT D6.CVLCPT_DOC_ID AS CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPFPL006M D6 WHERE D2.QSTNAI_YR=D6.QSTNAI_YR and D2.QSTNAI_NO=D6.QSTNAI_NO AND D6.NTAS_RQ_MTRL_DOC_KND_CD = 'A1') AS QSTNAIPDF_DOC_ID, ( SELECT D6.CVLCPT_DOC_ID AS CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPFPL006M D6 WHERE D2.QSTNAI_YR=D6.QSTNAI_YR and D2.QSTNAI_NO=D6.QSTNAI_NO AND D6.NTAS_RQ_MTRL_DOC_KND_CD = 'A2') AS QSTNAIHWP_DOC_ID, ( SELECT D6.CVLCPT_DOC_ID AS CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPFPL006M D6 WHERE D2.QSTNAI_YR=D6.QSTNAI_YR and D2.QSTNAI_NO=D6.QSTNAI_NO AND D6.NTAS_RQ_MTRL_DOC_KND_CD = 'A3') AS QSTNAIETC_DOC_ID, ( SELECT D7.CVLCPT_DOC_ID AS CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPFPL007M D7 WHERE D2.QSTNAI_YR=D7.QSTNAI_YR and D2.QSTNAI_NO=D7.QSTNAI_NO and D2.DTIL_ANS_NO=D7.DTIL_ANS_NO and D2.DTIL_DVSN_NO=D7.DTIL_DVSN_NO AND D7.NTAS_RQ_MTRL_DOC_KND_CD = 'B1') AS HC_DOC_ID, ( SELECT D7.CVLCPT_DOC_ID AS CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPFPL007M D7 WHERE D2.QSTNAI_YR=D7.QSTNAI_YR and D2.QSTNAI_NO=D7.QSTNAI_NO and D2.DTIL_ANS_NO=D7.DTIL_ANS_NO and D2.DTIL_DVSN_NO=D7.DTIL_DVSN_NO AND D7.NTAS_RQ_MTRL_DOC_KND_CD = 'B2') AS ETC_DOC_ID FROM TBCSPFPL002M D2 where 1=1 AND QSTNAI_YR=#strQstnaiYr# AND QSTNAI_NO=#strQstnaiNo# order by D2.DTIL_ANS_NO asc