select D2.WRTANS_NO AS WRTANS_NO,
    D3.QSTNAI_YR AS QSTNAI_YR,
    D3.QSTNAI_NO AS QSTNAI_NO,
    D3.SMT_TIT_NM AS SMT_TIT_NM,
    D1.SITT_NO AS SITT_NO,
    /*회기번호*/ D1.RQR_NM AS RQR_NM,
    /*요청자명*/ D2.QUE_TXT AS QUE_TXT,
    D2.TKCG_DEPT_NM AS TKCG_DEPT_NM /* 담당부서명 */,
    D3.WRTANS_REGI_DT AS WRTANS_REGI_DT,
    ( SELECT D7.CVLCPT_DOC_ID AS CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPFPL007M D7 WHERE D2.QSTNAI_YR=D7.QSTNAI_YR and D2.QSTNAI_NO=D7.QSTNAI_NO and D2.DTIL_ANS_NO=D7.DTIL_ANS_NO and D2.DTIL_DVSN_NO=D7.DTIL_DVSN_NO AND D7.NTAS_RQ_MTRL_DOC_KND_CD = 'B1') AS HC_DOC_ID, D2.ANS_MTRL_TXT AS ANS_MTRL_TXT, '답변자료' AS ANS_MTRL_TXT_BUT, '답변자료첨부파일' AS ETC_DOC_ID_BUT, ( SELECT D7.CVLCPT_DOC_ID AS CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPFPL007M D7 WHERE D2.QSTNAI_YR=D7.QSTNAI_YR and D2.QSTNAI_NO=D7.QSTNAI_NO and D2.DTIL_ANS_NO=D7.DTIL_ANS_NO and D2.DTIL_DVSN_NO=D7.DTIL_DVSN_NO AND D7.NTAS_RQ_MTRL_DOC_KND_CD = 'B2') AS ETC_DOC_ID, D3.GTR_TXT as WRTANS_TXT, '답변서' as WRTANS_TXT_BUT, '답변서첨부파일' as TOTAL_ETC_DOC_ID_BUT, ( SELECT D8.CVLCPT_DOC_ID AS CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPFPL008M D8 WHERE D3.QSTNAI_YR=D8.QSTNAI_YR and D3.QSTNAI_NO=D8.QSTNAI_NO and D3.WRTANS_NO=D8.WRTANS_NO AND D8.NTAS_RQ_MTRL_DOC_KND_CD = 'C2') AS TOTAL_ETC_DOC_ID from tbcspfpl002m D2, tbcspfpl003m D3, tbcspfpl001m D1 where 1=1 and D2.QSTNAI_YR = #strQstnaiYr# and D2.QSTNAI_NO = #strQstnaiNo# and D2.QSTNAI_YR = D3.QSTNAI_YR and D2.QSTNAI_NO = D3.QSTNAI_NO and D3.QSTNAI_YR = D1.QSTNAI_YR and D3.QSTNAI_NO = D1.QSTNAI_NO and D2.WRTANS_NO = D3.WRTANS_NO order by D2.WRTANS_NO asc;