SELECT A.QSTNAI_YR || '-' || A.QSTNAI_NO AS QSTNAI_YR,
    B.QSTNAI_YR||'-'||B.QSTNAI_NO||'-'||B.DTIL_ANS_NO AS QSTNAI_YR,
    A.SITT_NO,
    F_CODE_NM('1000092', A.NTAS_RQ_MTRL_DVSN_CD) AS F_CODE_NM,
    F_CODE_NM('1000091', A.CMT_CD) AS F_CODE_NM,
    A.SRECS_SRNG_MBCMT_NM /* 심사재심의심사위원명 */,
    A.RQR_NM,
    F_CODE_NM('1000093', A.RQ_SHP_CD) AS F_CODE_NM,
    TO_CHAR(TO_DATE(A.RQT_YMD), 'YYYY-MM-DD') AS RQT_YMD /* 요구일자 */,
    TO_CHAR(TO_DATE(A.NTAS_SMT_DDLN_DT), 'YYYY-MM-DD') AS TO_CHAR /* 제출기한 */,
    DECODE(NTAS_SMT_HNDL_STA_CD,'40',TO_CHAR(TO_DATE(B.NTAS_SMT_DT), 'YYYY-MM-DD'),F_CODE_NM ('1000661',B.NTAS_SMT_HNDL_STA_CD)) AS NTAS_SMT_HNDL_STA_CD /* 제출일자 */,
    B.QUE_TXT,
    ( SELECT AGGR_CONCAT( S1.TKCG_DEPT_NM, ', ') AS TKCG_DEPT_NM /* 담당부서명 */
FROM TBCSPFPL002M S1 WHERE B.QSTNAI_YR = S1.QSTNAI_YR AND B.QSTNAI_NO = S1.QSTNAI_NO AND B.DTIL_ANS_NO = S1.DTIL_ANS_NO ) AS TKCG_DEPT_NM ,( SELECT AGGR_CONCAT( S1.PIC_NM, ', ') AS PIC_NM /* 담당자명 */
FROM TBCSPFPL002M S1 WHERE B.QSTNAI_YR = S1.QSTNAI_YR AND B.QSTNAI_NO = S1.QSTNAI_NO AND B.DTIL_ANS_NO = S1.DTIL_ANS_NO ) AS PIC_NM ,TO_CHAR(TO_DATE(B.SBMSN_YMD), 'YYYY-MM-DD') AS SBMSN_YMD /* 각국제출일자 */ , F_CODE_NM ('1000661',B.NTAS_SMT_HNDL_STA_CD) AS NTAS_SMT_HNDL_STA_CD FROM TBCSPFPL001M A ,TBCSPFPL002M B WHERE 1=1 AND A.QSTNAI_YR = B.QSTNAI_YR AND A.QSTNAI_NO = B.QSTNAI_NO AND B.DTIL_DVSN_NO ='01' A.QSTNAI_YR = #strYear# A.QSTNAI_NO LIKE '%'||#strQstnai#||'%' A.QSTNAI_TIT_NM LIKE '%'||#strQstnaiTitleNm#||'%' A.SRECS_SRNG_MBCMT_NM LIKE '%'||#strCmtrNm#||'%' A.RQT_YMD >= #strRqDtFrom# A.RQT_YMD &lt;= #strRqDtTo# A.SMT_RQ_DT >= #strSmtRqDtFrom# A.SMT_RQ_DT &lt;= #strSmtRqDtTo# A.CMT_CD = #strCmtCd# B.NTAS_RQ_MTRL_HNDL_STA_CD = #strNtasRqMtrlHndlStaCd# B.TKCG_DEPT_NM = #TKCG_DEPT_NM# B.PIC_NM = #PIC_NM# ORDER BY NO ASC,DTIL_DVSN_NO ASC