SELECT A.PRPS_TIT_NM /* 제안제목 */,
    DECODE(A.PRPS_PEN_CNB,#strCNB#,F_USR_INFO(A.PRPS_PEN_CNB,'2'), NULL) AS PRPS_PEN_CNB /* 제안자명 */,
    DECODE(A.PRPS_PEN_CNB,#strCNB#,A.PRPS_PEN_CNB,NULL) AS PRPS_PEN_CNB /* 제안자전산번호 */,
    DECODE(A.PRPS_PEN_CNB,#strCNB#,B.JNT_PRPS_PEN_NAM,NULL) AS PRPS_PEN_CNB /* 공동제안자 */,
    F_CODE_NM('1000075',A.PRPS_STA_CD) AS F_CODE_NM,
    A.PRPS_STA_CD /* 제안상태코드 */,
    A.CUR_SYE_TXT /* 현행제도내용 */,
    A.PRB_TXT /* 문제점내용 */,
    A.IMP_ATV_TXT /* 개선대안내용 */,
    A.IMP_EFCT_TXT /* 개선효과내용 */,
    A.PRPS_YE /* 제안연도 */,
    A.PRPS_SLN /* 제안연번 */,
    (SELECT Z.CVLCPT_DOC_ID AS CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPGSU004M Z WHERE Z.PRPS_YE = A.PRPS_YE AND Z.PRPS_SLN = A.PRPS_SLN AND Z.PRPS_ATT_FL_DVSN_CD = '1') AS CVLCPT_DOC_ID /*첨부파일 문서*/ ,F_DPT_INFO(A.EXAM_DPT_CD,'1') AS EXAM_DPT_NM /*검토부서*/ ,A.EXAM_DT /*검토일자*/ ,DECODE(A.PRPS_PEN_CNB,#strCNB#,F_USR_INFO(A.EXAM_PEN_CNB,'2'),NULL) AS EXAM_PEN_NAM /*검토자*/ ,A.EXAM_TXT ,F_CODE_NM('1000077',A.EXAM_RSLT_CD) AS EXAM_RSLT_NM ,(SELECT Z.CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPGSU004M Z WHERE Z.PRPS_YE = A.PRPS_YE AND Z.PRPS_SLN = A.PRPS_SLN AND Z.PRPS_ATT_FL_DVSN_CD = '2') AS EXAM_DOC_ID /*검토첨부파일 문서*/ ,DECODE(A.PRPS_STA_CD,'M','종료(불채택)','N','종료(채택)','') AS PRPS_STA_YN /*채택여부*/ ,A.JDG_DT /*위원회일자*/ ,F_CODE_NM('1000076',A.RK_CD) AS RK_NM ,A.PRPS_ASC ,F_CODE_NM('1000286',A.CNTR_PRPS_RCMD_DVSN_CD) AS CNTR_PRPS_RCMD_DVSN_NM ,A.PRPS_DPT_CD FROM TBCSPGSU001M A /*도서자료기본*/ , ( SELECT PRPS_YE,
    PRPS_SLN,
    AGGR_CONCAT(F_USR_INFO (A.PRPS_PEN_CNB,'2'),'/') AS AGGR_CONCAT
FROM TBCSPGSU002L A WHERE 1=1 AND A.PRPS_YE = #strPRPS_YE# AND A.PRPS_SLN = #strPRPS_SLN# AND A.GRNDS_EXMN_AEX_SQNO <> 1 GROUP BY PRPS_YE, PRPS_SLN) B WHERE 1=1 AND A.PRPS_YE = #strPRPS_YE# AND A.PRPS_SLN = #strPRPS_SLN# AND A.PRPS_YE = B.PRPS_YE(+) AND A.PRPS_SLN = B.PRPS_SLN(+)