UPDATE TBCSPGSU001M SET NTFCTN_RCPT_YMD = TO_CHAR(SYSDATE,'YYYYMMDD') /*제출일자*/ , PRPS_STA_CD = 'C' /*진행상태 = C : 접수*/ , PRPS_ACP_NO = (SELECT TO_CHAR(NVL(MAX(TO_NUMBER(PRPS_ACP_NO))+1,1)) AS TO_CHAR
FROM TBCSPGSU001M WHERE PRPS_YE = #PRPS_YE# ) , LAST_USER_ID = #LAST_USER_ID# /*최종사용자ID*/ , LAST_PRCS_DEPT_CD = #LAST_PRCS_DEPT_CD# /*최종부서*/ , LAST_MENU_ID = #LAST_MENU_ID# /*최종메뉴*/ , LAST_MDFCN_DT = SYSDATE /*최종수정일시*/ WHERE PRPS_YE = #PRPS_YE# AND PRPS_SLN = #PRPS_SLN#