SELECT DECODE(A.PRPS_ACP_NO,NULL,'',PRPS_YE||'-'||A.PRPS_ACP_NO) AS PRPS_ACP_NO /* 접수번호 */,
    TO_CHAR(TO_DATE(A.NTFCTN_RCPT_YMD), 'YYYY-MM-DD') AS NTFCTN_RCPT_YMD /* 통보접수일자 */,
    A.PRPS_TIT_NM /* 제안제목 */,
    F_USR_INFO(A.PRPS_PEN_CNB,'2') AS F_USR_INFO /* 제안자명 */,
    F_DPT_INFO(A.PRPS_DPT_CD, '1') AS F_DPT_INFO,
    F_DPT_INFO(A.EXAM_DPT_CD,'1') AS F_DPT_INFO,
    CASE WHEN A.PRPS_STA_CD IN ('M','N','O') THEN TO_CHAR(A.LAST_MDFCN_DT, 'YYYY-MM-DD') ELSE '' END AS LAST_MDFCN_DT /* 최종수정일시 */,
    F_CODE_NM('1000075', A.PRPS_STA_CD) AS F_CODE_NM
FROM TBCSPGSU001M A /*도서자료기본*/ WHERE 1=1 AND A.PRPS_STA_CD &gt;= 'B' AND A.PRPS_STA_CD = #strPRPS_STA_CD# AND (A.NTFCTN_RCPT_YMD &gt;= #strACP_DTS# OR A.NTFCTN_RCPT_YMD IS NULL) AND (A.NTFCTN_RCPT_YMD &lt;= #strACP_DTE# OR A.NTFCTN_RCPT_YMD IS NULL) AND A.PRPS_PEN_CNB = #strPRPS_PEN_CNB# AND A.PRPS_TIT_NM LIKE '%'||#strPRPS_TIT_NM#||'%' ORDER BY TO_NUMBER(A.PRPS_YE) DESC , TO_NUMBER(A.PRPS_ACP_NO) DESC