SELECT DECODE(A.PRPS_ACP_NO,NULL,'',A.PRPS_YE||'-'||A.PRPS_ACP_NO) AS PRPS_ACP_NO /* 접수번호 */,
    A.PRPS_TIT_NM,
    A.PRPS_KND_NM,
    A.CHOS_YE,
    F_CODE_NM('1000076',A.RK_CD) AS F_CODE_NM,
    F_USR_INFO(A.PRPS_PEN_CNB,'2') AS F_USR_INFO /* 제안자명 */,
    F_CODE_NM('1000173',F_USR_INFO(A.PRPS_PEN_CNB,'3')) AS F_CODE_NM /* 제안자 직급(직위) */,
    F_DPT_INFO(F_USR_INFO(A.PRPS_PEN_CNB,'5'),1) AS F_DPT_INFO,
    (SELECT AS CTB_RT /* 기여비율 */
FROM TBCSPGSU002L Z WHERE A.PRPS_YE = Z.PRPS_YE AND A.PRPS_SLN = Z.PRPS_SLN AND A.PRPS_PEN_CNB = Z.PRPS_PEN_CNB) AS PRPS_CTB_RTE ,B.JNT_PRPS_PEN_NAM /*공동제안자*/ ,B.JNT_PRPS_DPT_NM /*공동제안자*/ ,B.JNT_PRPS_RANK_NM /*공동제안자 직급*/ ,B.JNT_PRPS_CTB_RTE /*공동제안자 기여율*/ ,F_DPT_INFO(A.EXEC_DPT_CD,'1') AS EXEC_DPT_NM ,A.RWD_KND_NM ,A.PRZ_NM ,A.HRA_PRIV_KND_NM ,TO_CHAR(TO_DATE(A.HRA_PRIV_DT),'YYYY-MM-DD') AS HRA_PRIV_DT ,TO_CHAR(TO_DATE(A.EXEC_DT),'YYYY-MM-DD') AS EXEC_DT ,A.EXEC_BAS_TXT ,F_CODE_NM('1000287',A.EXEC_DVSN_CD) AS EXEC_DVSN_NM ,A.EXEC_TXT FROM TBCSPGSU001M A /*도서자료기본*/ , ( SELECT PRPS_YE,
    PRPS_SLN,
    AGGR_CONCAT(F_USR_INFO (A.PRPS_PEN_CNB,'2'),'/') AS AGGR_CONCAT,
    AGGR_CONCAT(A.CTB_RT,'/') AS AGGR_CONCAT,
    AGGR_CONCAT(F_DPT_INFO(F_USR_INFO(A.PRPS_PEN_CNB,'5'),1),'/') AS AGGR_CONCAT,
    AGGR_CONCAT(F_CODE_NM('1000173',F_USR_INFO(A.PRPS_PEN_CNB,'3')),'/') AS AGGR_CONCAT
FROM TBCSPGSU002L A WHERE 1=1 AND A.PRPS_YE = #strPRPS_YE# AND A.PRPS_SLN = #strPRPS_SLN# AND A.GRNDS_EXMN_AEX_SQNO <> 1 GROUP BY PRPS_YE, PRPS_SLN) B WHERE 1=1 AND A.PRPS_YE = #strPRPS_YE# AND A.PRPS_SLN = #strPRPS_SLN# AND A.PRPS_YE = B.PRPS_YE(+) AND A.PRPS_SLN = B.PRPS_SLN(+)