SELECT DECODE(A.PRPS_ACP_NO,NULL,'',A.PRPS_YE||'-'||A.PRPS_ACP_NO) AS PRPS_ACP_NO /* 접수번호 */,
    A.NTFCTN_RCPT_YMD /* 통보접수일자 */,
    A.SBMSN_YMD /* 제출일자 */,
    A.PRPS_TIT_NM /* 제안제목 */,
    F_USR_INFO(A.PRPS_PEN_CNB,'2') AS F_USR_INFO /* 제안자명 */,
    A.PRPS_PEN_CNB /* 제안자전산번호 */,
    (SELECT CASE WHEN Z.GRNDS_EXMN_AEX_SQNO = 1 THEN '' WHEN Z.GRNDS_EXMN_AEX_SQNO = 2 THEN F_USR_INFO(Z.PRPS_PEN_CNB,'2') WHEN Z.GRNDS_EXMN_AEX_SQNO >= 3 THEN F_USR_INFO(Z.PRPS_PEN_CNB,'2')||' 외 '||(Z.GRNDS_EXMN_AEX_SQNO-2)||'명' ELSE '' AS GRNDS_EXMN_AEX_SQNO /* 현장조사수용비순번 */
FROM TBCSPGSU002L Z WHERE B.PRPS_YE = Z.PRPS_YE AND B.PRPS_SLN = Z.PRPS_SLN AND B.CNT = Z.GRNDS_EXMN_AEX_SQNO) AS JNT_PRPS_PEN_NAM /*공동제안자*/ ,F_CODE_NM('1000075',A.PRPS_STA_CD) AS PRPS_STA_NM ,A.PRPS_STA_CD /*제안상태코드*/ ,A.CUR_SYE_TXT /*현행제도내용*/ ,A.PRB_TXT /*문제점내용*/ ,A.IMP_ATV_TXT /*개선대안내용*/ ,A.IMP_EFCT_TXT /*개선효과내용*/ ,A.PRPS_YE /*제안연도*/ ,A.PRPS_SLN /*제안연번*/ ,(SELECT Z.CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPGSU004M Z WHERE Z.PRPS_YE = A.PRPS_YE AND Z.PRPS_SLN = A.PRPS_SLN AND Z.PRPS_ATT_FL_DVSN_CD = '1') AS CVLCPT_DOC_ID /*첨부파일 문서*/ FROM TBCSPGSU001M A /*도서자료기본*/ , ( SELECT PRPS_YE,
    PRPS_SLN,
    COUNT(*) AS CNT
FROM TBCSPGSU002L A WHERE 1=1 AND A.PRPS_YE = #strPRPS_YE# AND A.PRPS_SLN = #strPRPS_SLN# GROUP BY PRPS_YE, PRPS_SLN) B WHERE 1=1 AND A.PRPS_YE = #strPRPS_YE# AND A.PRPS_SLN = #strPRPS_SLN# AND A.PRPS_YE = B.PRPS_YE AND A.PRPS_SLN = B.PRPS_SLN