SELECT T1.BZTRP_YR /* 출장년도 */,
    T1.FRS_MDM_SQNO /* 포렌식매체순번 */,
    T1.BZT_TIT,
    T1.SRECS_DEPT_CD /* 심사재심의부서코드 */,
    F_DPT_INFO(T1.SRECS_DEPT_CD,'1') AS CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */,
    T1.BGT_DVSN /* 예산구분 */,
    T1.BZT_TRDS,
    T1.BZT_TRDE,
    TO_CHAR(TO_DATE(T1.BZT_TRDS, 'YYYYMMDD'), 'YYYY.MM.DD') ||' ~ '|| TO_CHAR(TO_DATE(T1.BZT_TRDE, 'YYYYMMDD'), 'YYYY.MM.DD') AS TO_CHAR,
    (SELECT AS BZT_NAT_CD
FROM TBCSPHIN004N WHERE FRS_MDM_SQNO = T1.FRS_MDM_SQNO AND BZTRP_YR = T1.BZTRP_YR AND ROWNUM=1) AS BZT_NAT ,CASE WHEN (SELECT COUNT(BZT_NAT_CD)
FROM TBCSPHIN004N WHERE FRS_MDM_SQNO = T1.FRS_MDM_SQNO AND BZTRP_YR = T1.BZTRP_YR) > 1 THEN ((SELECT F_CODE_NM('1000181',BZT_NAT_CD)
FROM TBCSPHIN004N WHERE FRS_MDM_SQNO = T1.FRS_MDM_SQNO AND BZTRP_YR = T1.BZTRP_YR AND ROWNUM=1) || ' 외 ' || (SELECT NVL(COUNT(*)-1,0)
FROM TBCSPHIN004N WHERE FRS_MDM_SQNO = T1.FRS_MDM_SQNO AND BZTRP_YR = T1.BZTRP_YR) || '개국' ) ELSE (SELECT F_CODE_NM('1000181',BZT_NAT_CD)
FROM TBCSPHIN004N WHERE FRS_MDM_SQNO = T1.FRS_MDM_SQNO AND BZTRP_YR = T1.BZTRP_YR ) END AS BZT_NAT_NM /* 출장국가명 */ ,(SELECT BZT_CNB
FROM TBCSPHIN004C WHERE FRS_MDM_SQNO = T1.FRS_MDM_SQNO AND BZTRP_YR = T1.BZTRP_YR AND ROWNUM=1) AS BZT_CNB ,CASE WHEN (SELECT COUNT(BZT_CNB)
FROM TBCSPHIN004C WHERE FRS_MDM_SQNO = T1.FRS_MDM_SQNO AND BZTRP_YR = T1.BZTRP_YR) + TO_NUMBER(NVL(T1.NBAI_BZT_NUM,0)) > 1 THEN ((SELECT F_USR_INFO(BZT_CNB, '2')
FROM TBCSPHIN004C WHERE FRS_MDM_SQNO = T1.FRS_MDM_SQNO AND BZTRP_YR = T1.BZTRP_YR AND ROWNUM=1) || ' 외 ' || (SELECT NVL(COUNT(*)-1,0) + TO_NUMBER(NVL(T1.NBAI_BZT_NUM,0))
FROM TBCSPHIN004C WHERE FRS_MDM_SQNO = T1.FRS_MDM_SQNO AND BZTRP_YR = T1.BZTRP_YR) || '명' ) ELSE (SELECT F_USR_INFO(BZT_CNB, '2')
FROM TBCSPHIN004C WHERE FRS_MDM_SQNO = T1.FRS_MDM_SQNO AND BZTRP_YR = T1.BZTRP_YR ) END AS BZT_NAM /* 출장자명 */ ,T1.JDG_TG /* 심사대상여부 */ ,CASE WHEN T1.BZT_PLN_DOC_ID IS NOT NULL THEN '[출장계획서]' ELSE NULL END AS BZT_PLN_DOC_NM /* 출장 계획서명 */ ,T1.BZT_PLN_DOC_ID /* 출장 계획서 문서 ID*/ ,(SELECT CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPHIN004L WHERE FRS_MDM_SQNO = T1.FRS_MDM_SQNO AND BZTRP_YR = T1.BZTRP_YR ) AS BZT_RSLT_RPRT_DOC_ID /* 출장 결과보고서 ID*/ /* 출장종료 후 결과보고 30일이내 보고서가 있으면 보고서를 없으면 남은날짜표시 */ ,CASE WHEN (TO_DATE(T1.BZT_TRDE, 'YYYYMMDD') AND T1.BZTRP_YR = #BZTRP_YR# AND T1.FRS_MDM_SQNO = #FRS_MDM_SQNO# AND T1.SRECS_DEPT_CD = #SRECS_DEPT_CD# AND #strBZT_NAT_CD# IN (SELECT BZT_NAT_CD
FROM TBCSPHIN004N WHERE FRS_MDM_SQNO = T1.FRS_MDM_SQNO AND BZTRP_YR = T1.BZTRP_YR) AND #strBZT_CNB# IN (SELECT BZT_CNB
FROM TBCSPHIN004C WHERE FRS_MDM_SQNO = T1.FRS_MDM_SQNO AND BZTRP_YR = T1.BZTRP_YR) AND T1.JDG_TG = #strJDG_TG# AND T1.HIRANKPOS = #strHIRANKPOS# AND (SELECT CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPHIN004L WHERE FRS_MDM_SQNO = T1.FRS_MDM_SQNO AND BZTRP_YR = T1.BZTRP_YR) IS NOT NULL AND (SELECT CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPHIN004L WHERE FRS_MDM_SQNO = T1.FRS_MDM_SQNO AND BZTRP_YR = T1.BZTRP_YR) IS NULL T1.BZT_TIT LIKE '%'||#strBZT_TIT# ||'%' AND (T1.BZT_TRDS = #strBZT_TRDS# AND T1.BZT_TRDE >= #strBZT_TRDE# OR T1.BZT_TRDS BETWEEN #strBZT_TRDS# AND #strBZT_TRDE# OR T1.BZT_TRDE BETWEEN #strBZT_TRDS# AND #strBZT_TRDE#)