SELECT NTCR_NM /* 게시자명 */,
    FRST_REGIST_PNTTM /* 최초등록시점 */,
    NTT_SJ /* 게시물제목 */,
    CASE WHEN SUB1 IS NOT NULL AND NTT_CN IS NOT NULL AND SUB2 IS NOT NULL THEN TO_CLOB(SUB1) || CHR(13) || CHR(10) || TO_CLOB(NTT_CN) || CHR(13) || CHR(10) || TO_CLOB(SUB2) WHEN SUB1 IS NOT NULL AND NTT_CN IS NOT NULL AND SUB2 IS NULL THEN TO_CLOB(SUB1) || CHR(13) || CHR(10) || TO_CLOB(NTT_CN) WHEN SUB1 IS NOT NULL AND NTT_CN IS NULL AND SUB2 IS NOT NULL THEN TO_CLOB(SUB1) || CHR(13) || CHR(10) || TO_CLOB(SUB2) WHEN SUB1 IS NULL AND NTT_CN IS NOT NULL AND SUB2 IS NOT NULL THEN TO_CLOB(NTT_CN) || CHR(13) || CHR(10) || TO_CLOB(SUB2) WHEN SUB1 IS NOT NULL AND NTT_CN IS NULL AND SUB2 IS NULL THEN TO_CLOB(SUB1) WHEN SUB1 IS NULL AND NTT_CN IS NOT NULL AND SUB2 IS NULL THEN TO_CLOB(NTT_CN) WHEN SUB1 IS NULL AND NTT_CN IS NULL AND SUB2 IS NOT NULL THEN TO_CLOB(SUB2) ELSE TO_CLOB('') END AS SUB1 /* 게시물내용 */,
    CVLCPT_DOC_ID /* 민원문서아이디 */,
    F_ORG_INFO(REL_INST_CD,'1') AS F_ORG_INFO /* 관련기관명 */
FROM TBCSPTSM002L WHERE 1=1 AND BBS_ID='BBSMSTR_100000000032' AND USE_AT = 'Y' AND NTT_ID = #strNttId#