SELECT SRECS_DEPT_CD /* 심사재심의부서코드 */,
    CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */,
    HIRK_DPT_CD
FROM TBDCMACM002M WHERE 1=1 SRECS_DEPT_CD IN ( SELECT SRECS_DEPT_CD /* 심사재심의부서코드 */
FROM TABLE(F_AUTH_DEPT(#strCnb#, #strDptAuth#, 'Y'))) AND SUBSTR(SRECS_DEPT_CD, 0, 3) = SUBSTR(#strHrkDptCd#, 0, 3) OR SUBSTR(SRECS_DEPT_CD, 0, 3) = SUBSTR(#strHrkDptCd#, 0, 3) AND USE_YN = 'Y' AND SUBSTR(SRECS_DEPT_CD, 4, 6) != '000' AND SUBSTR(SRECS_DEPT_CD, 1, 4) != '1901' AND SRECS_DEPT_CD NOT IN( '150160', '150170', '150180', '150190', '770770', '880880') AND SRECS_DEPT_CD NOT IN('140100', '140150', '140170') AND SRECS_DEPT_CD NOT IN('080170', '080180', '080190', '080200', '080210', '080220') AND SRECS_DEPT_CD NOT IN('0I0100', '0I0200', '0I0210', '0I0220') AND SUBSTR(SRECS_DEPT_CD, 1, 3) NOT IN ('121','122','140','141', '100', '160', '1A0', '170', '200', '300','400') ORDER BY SRECS_DEPT_CD