SELECT SRECS_DEPT_CD /* 심사재심의부서코드 */,
    CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */
FROM TBDCMACM002M WHERE USE_YN = 'Y' AND SUBSTR(SRECS_DEPT_CD, 4, 6) != '000' AND SUBSTR(SRECS_DEPT_CD, 1, 4) != '1901' AND SRECS_DEPT_CD NOT IN('150150', '150160', '150170', '770770', '880880') AND SRECS_DEPT_CD NOT IN('140100', '140150', '140170') AND SRECS_DEPT_CD NOT IN('080170', '080180', '080190', '080200', '080210', '080220') AND SRECS_DEPT_CD NOT IN('0I0100', '0I0200', '0I0210', '0I0220') AND SUBSTR(SRECS_DEPT_CD, 1, 3) NOT IN ('141', '100', '160', '1A0', '170', '200', '300', '150') ORDER BY SRECS_DEPT_CD