SELECT A.RQS_YE||'-'||A.RQS_SLN AS RQS_YE,
    /*요청연도, 요청연번*/ A.DMND_YMD AS DMND_YMD /* 요청일자 */,
    /*요청일자*/ A.RQS_SER_CD,
    /*요청부문코드*/ (SELECT Z.CNSTN_MBCMT_DEPT_NM AS CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */
FROM TBDCMACM002M Z WHERE A.RQSTR_DEPT_CD = Z.SRECS_DEPT_CD) AS RQSTR_DEPT_CD, /*요청국과*/ A.RQR_NM, /*요청자*/ A.DMND_CN, /*요청내용*/ A.EOR_HND_DT, /*장애조치일*/ A.EOR_HND_TXT, /*조치내용*/ A.RQS_YE, /*요청연도*/ A.RQS_SLN, /*요청연번*/ A.HND_STA_CD, /*조치상태코드*/ A.HND_PEN_NM, A.DRTR_CNB, /*책임자전산번호*/ A.DRTR_NAM /*책임자명*/ FROM TBCSPIIT001M A /*PC장비관리테이블*/ WHERE 1=1 AND A.DMND_YMD &gt;= #strRQS_DTS# AND A.DMND_YMD &lt;= #strRQS_DTE# AND A.IPTC_CD = #strIPTC_CD# AND A.HND_STA_CD = #strHND_STA_CD# AND A.EOR_HND_DT = #strEOR_HND_DT# AND A.RQS_SER_CD = #strRQS_SER_CD# AND A.HND_PEN_NM LIKE '%'||#strHND_PEN_NM#||'%' AND A.RQSTR_DEPT_CD = #RQSTR_DEPT_CD# AND (A.DMND_CN LIKE '%'||#strSEARCH_NM#||'%' OR A.EOR_HND_TXT LIKE '%'||#strSEARCH_NM#||'%' OR A.RQR_NM LIKE '%'||#strSEARCH_NM#||'%') AND A.DRTR_NAM LIKE '%'||#strDRTR_NAM#||'%' ORDER BY A.RQS_YE DESC, A.RQS_SLN DESC