SELECT TO_CHAR(TO_DATE(A.DMND_YMD),'YYYY-MM-DD') AS DMND_YMD /* 요청일자 */,
    /*요청일자*/ F_CODE_NM('1000327',A.RQS_SER_CD) AS F_CODE_NM,
    /*요청부문코드*/ (SELECT Z.CNSTN_MBCMT_DEPT_NM AS CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */
FROM TBDCMACM002M Z WHERE A.RQSTR_DEPT_CD = Z.SRECS_DEPT_CD) AS RQR_DPT_NM, /*요청국과*/ A.RQR_NM, /*요청자*/ A.DMND_CN, /*요청내용*/ A.EOR_HND_TXT /*조치내용*/ FROM TBCSPIIT001M A /*PC장비관리테이블*/ WHERE 1=1 AND A.DMND_YMD &gt;= #strRQS_DTS# AND A.DMND_YMD &lt;= #strRQS_DTE# AND A.IPTC_CD = #strIPTC_CD# AND A.HND_STA_CD = #strHND_STA_CD# AND A.EOR_HND_DT = #strEOR_HND_DT# AND A.RQS_SER_CD = #strRQS_SER_CD# AND A.HND_PEN_NM LIKE '%'||#strHND_PEN_NM#||'%' AND A.RQSTR_DEPT_CD = #RQSTR_DEPT_CD# AND (A.DMND_CN LIKE '%'||#strSEARCH_NM#||'%' OR A.EOR_HND_TXT LIKE '%'||#strSEARCH_NM#||'%' OR A.RQR_NM LIKE '%'||#strSEARCH_NM#||'%') ORDER BY A.RQS_YE DESC, A.RQS_SLN DESC