SELECT A.RQS_YE,
    /*요청연도*/ A.RQS_SLN,
    /*요청연번*/ A.RQS_SER_CD,
    /*요청부문코드*/ A.IPTC_CD,
    /*조회수*/ A.DMND_YMD AS DMND_YMD /* 요청일자 */,
    /*요청일*/ A.RQSTR_DEPT_CD AS RQSTR_DEPT_CD /* 요청자부서코드 */,
    /*요청자부서코드*/ (SELECT Z.CNSTN_MBCMT_DEPT_NM AS CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */
FROM TBDCMACM002M Z WHERE A.RQSTR_DEPT_CD = Z.SRECS_DEPT_CD ) AS RQR_DPT_NM, /*부서명*/ A.RQSTR_ENO, /*요청자전산번호*/ A.RQR_NM, /*요청자명*/ A.RQS_TIT_NM, /*요청제목명*/ A.DMND_CN, /*요청내용*/ A.EOR_HND_DT, /*장애조치일*/ A.HND_PEN_CNB, /*조치자전산번호*/ A.HND_PEN_NM, /*조치자명*/ A.HND_STA_CD, /*조치상태코드*/ A.EOR_HND_TXT, /*장애조치내용*/ A.RQR_TNB, /*요청자전화번호*/ A.HND_PEN_TNB, /*조치자전화번호*/ A.HND_PEN_CNB AS OLD_HNDL_PEN_CNB, /*조치자전산번호*/ NVL(A.SEN_YN,'N') AS SEN_YN, (SELECT CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPIIT008M Z WHERE Z.RQS_YE = A.RQS_YE AND Z.RQS_SLN = A.RQS_SLN AND Z.EOR_ATT_FL_DVSN_CD = '1') AS DMND_DOC_ID, (SELECT CVLCPT_DOC_ID /* 민원문서아이디 */
FROM TBCSPIIT008M Z WHERE Z.RQS_YE = A.RQS_YE AND Z.RQS_SLN = A.RQS_SLN AND Z.EOR_ATT_FL_DVSN_CD = '2') AS HND_DOC_ID, A.DRTR_CNB, /*책임자전산번호*/ A.DRTR_NAM, /*책임자명*/ (SELECT USR_DFN_TXT_1
FROM TBDCMACM013C WHERE CMM_CD_DVSN_CD='1000882' AND CD='4') AS CHAR_CNB, /*기본책임자전산번호*/ (SELECT USR_DFN_TXT_2
FROM TBDCMACM013C WHERE CMM_CD_DVSN_CD='1000882' AND CD='4') AS CHAR_NAM /*기본책임자명*/ FROM TBCSPIIT001M A WHERE A.RQS_YE = #strRQS_YE# AND A.RQS_SLN = #strRQS_SLN#