SELECT A.RQS_YE,
    /*요청연도*/ A.RQS_SLN,
    /*요청연번*/ F_CODE_NM('1000327',A.RQS_SER_CD) AS F_CODE_NM,
    /*요청부문코드*/ F_CODE_NM('1000063',A.IPTC_CD) AS F_CODE_NM,
    /*조회수*/ TO_CHAR(TO_DATE(A.DMND_YMD),'YYYY-MM-DD') AS DMND_YMD /* 요청일자 */,
    /*요청일*/ (SELECT Z.CNSTN_MBCMT_DEPT_NM AS CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */
FROM TBDCMACM002M Z WHERE A.RQSTR_DEPT_CD = Z.SRECS_DEPT_CD ) AS RQR_DPT_NM, /*부서명*/ A.RQR_NM, /*요청자명*/ A.RQS_TIT_NM, /*요청제목명*/ A.DMND_CN, /*요청내용*/ TO_CHAR(TO_DATE(A.EOR_HND_DT),'YYYY-MM-DD') AS EOR_HND_DT, /*장애조치일*/ A.HND_PEN_NM, /*조치자명*/ F_CODE_NM('1000295',A.HND_STA_CD) AS HND_STA_NM, /*조치상태코드*/ A.EOR_HND_TXT, /*장애조치내용*/ A.RQR_TNB, /*요청자전화번호*/ A.HND_PEN_TNB /*조치자전화번호*/ FROM TBCSPIIT001M A WHERE A.RQS_YE = #strRQS_YE# AND A.RQS_SLN = #strRQS_SLN#