SELECT A.PC_MNG_CD,
    /*PC관리코드*/ A.PC_SE_CAT_CD,
    /*PC용도분류코드*/ A.PSSN_DVSN_CD,
    /*소유구분코드*/ A.PC_USE_DVSN_CD,
    /*PC사용구분코드*/ ( SELECT AS CD_NM
FROM TBDCMACM013C S1 WHERE S1.CMM_CD_DVSN_CD ='1000144' AND S1.CD <> '000000000' AND S1.CD = A.SRECS_DEPT_CD ) AS CNSTN_MBCMT_DEPT_NM, ( SELECT S1.USER_NM /* 사용자명 */
FROM TBDCMACM001M S1 WHERE S1.TRGT_NOP_ENO = A.TRGT_NOP_ENO ) AS USER_NM, A.PC_KND_CD, /*PC종류코드*/ A.PC_EQP_CD, /*PC장비코드*/ A.PC_SRNO, /*시리얼번호*/ A.ITDC_DT, /*도입일*/ A.ISL_DT, /*설치일*/ A.STRG_DT, /*보관일*/ A.ABRG_DT, /*폐기일*/ A.PC_MNG_RMK, /*비고*/ A.TRGT_NOP_ENO, /*전산번호*/ A.SRECS_DEPT_CD, /*부서코드*/ A.ETC_MTTR_CN, A.TRGT_NOP_ENO AS OLD_CNB, ( SELECT S1.USER_NM /* 사용자명 */
FROM TBDCMACM001M S1 WHERE S1.TRGT_NOP_ENO = A.TRGT_NOP_ENO ) AS OLD_USR_NAM, A.SRECS_DEPT_CD AS OLD_DPT_CD, A.PC_USE_DVSN_CD AS OLD_PC_USE_DVSN_CD, (SELECT Z.CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */
FROM TBDCMACM002M Z WHERE A.SRECS_DEPT_CD = Z.SRECS_DEPT_CD) AS OLD_DPT_NM, ( SELECT CD_NM
FROM TBDCMACM013C S1 WHERE S1.CMM_CD_DVSN_CD ='1000173' AND S1.CD <> '000000000' AND S1.CD = (SELECT S2.JBGD_SECD /* 직급구분코드 */
FROM TBDCMACM001M S2 WHERE S2.TRGT_NOP_ENO = A.TRGT_NOP_ENO ) ) AS JBGD_NM ,MNT_DVSN_CD ,( SELECT CD_NM
FROM TBDCMACM013C S1 WHERE S1.CMM_CD_DVSN_CD ='1000800' AND S1.CD <> '000000000' AND S1.CD = A.MNT_DVSN_CD ) AS MNT_DVSN_NM ,MNT_DVSN_CD2 /*모니터2 추가 20170706 EUNOK*/ ,( SELECT CD_NM
FROM TBDCMACM013C S1 WHERE S1.CMM_CD_DVSN_CD ='1000800' AND S1.CD <> '000000000' AND S1.CD = A.MNT_DVSN_CD2 ) AS MNT_DVSN_NM2 ,(NVL2(A.IP_AD_1, A.IP_AD_1 || '.', '') || NVL2(A.IP_AD_2, A.IP_AD_2 || '.', '') || NVL2(A.IP_AD_3, A.IP_AD_3 || '.', '') || NVL2(A.IP_AD_4, A.IP_AD_4 , '') ) AS PC_IP /*IP 20170707 EUNOK*/ ,A.ASST_NO FROM TBCSPIIT005M A /*PC장비관리테이블*/ WHERE 1=1 AND A.PC_KND_CD = #strPC_KND_CD# AND A.PC_USE_DVSN_CD IN ('F','K','Y') AND A.PC_USE_DVSN_CD = #strPC_USE_DVSN_CD# AND A.SRECS_DEPT_CD = #SRECS_DEPT_CD# AND A.PC_EQP_CD = #strPC_EQP_CD# AND UPPER(A.PC_MNG_CD) LIKE '%'||UPPER(#strPC_MNG_CD#)||'%' AND A.PC_SE_CAT_CD = #strPC_SE_CAT_CD# AND A.PSSN_DVSN_CD = #strPSSN_DVSN_CD# AND ((SELECT S1.USER_NM /* 사용자명 */
FROM TBDCMACM001M S1 WHERE S1.TRGT_NOP_ENO = A.TRGT_NOP_ENO ) LIKE '%'||#strSEARCH_NM#||'%' OR UPPER(A.PC_MNG_CD) LIKE '%' ||UPPER(#strSEARCH_NM#)||'%' OR A.TRGT_NOP_ENO LIKE '%'||#strSEARCH_NM#||'%' OR UPPER(A.PC_SRNO) LIKE '%'||UPPER(#strSEARCH_NM#)||'%' OR UPPER(A.ASST_NO) LIKE '%'||UPPER(#strSEARCH_NM#)||'%' OR (NVL2(A.IP_AD_1, A.IP_AD_1 || '.', '') || NVL2(A.IP_AD_2, A.IP_AD_2 || '.', '') || NVL2(A.IP_AD_3, A.IP_AD_3 || '.', '') || NVL2(A.IP_AD_4, A.IP_AD_4 , '') ) LIKE '%'|| #strSEARCH_NM# ||'%' OR UPPER(A.PC_MNG_RMK) LIKE '%'||UPPER(#strSEARCH_NM#)||'%' OR UPPER(A.ETC_MTTR_CN) LIKE '%'||UPPER(#strSEARCH_NM#)||'%' ) AND (A.MNT_DVSN_CD = #strMNT_DVSN_CD3# OR A.MNT_DVSN_CD2 = #strMNT_DVSN_CD3# )/* 모니터모델 변경 strMNT_DVSN_CD-> strMNT_DVSN_CD3 20170714 EUNOK*/ AND TO_CHAR(TO_DATE(ITDC_DT),'YYYYMMDD') >= #strINS_DT# /*도입 시작일 20170707 EUNOK*/ /*도입 종료일 20170707 EUNOK*/ AND TO_CHAR(TO_DATE(ITDC_DT),'YYYYMMDD') ORDER BY A.PC_MNG_CD DESC ORDER BY A.PC_EQP_CD DESC ORDER BY A.SRECS_DEPT_CD DESC