SELECT A.PC_MNG_CD,
    /*PC관리코드*/ F_CODE_NM('1000306',A.PC_SE_CAT_CD) AS F_CODE_NM,
    /*PC용도분류코드*/ F_CODE_NM('1000307',A.PSSN_DVSN_CD) AS F_CODE_NM,
    /*소유구분코드*/ F_CODE_NM('1000305',A.PC_USE_DVSN_CD) AS F_CODE_NM,
    /*PC사용구분코드*/ F_CODE_NM('1000144',A.SRECS_DEPT_CD) AS CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */,
    /*부서코드*/ F_USR_INFO(A.TRGT_NOP_ENO,'2') AS USER_NM /* 사용자명 */,
    /*사용자*/ F_CODE_NM('1000170',A.PC_KND_CD) AS F_CODE_NM,
    /*PC종류코드*/ F_CODE_NM('1000148',A.PC_EQP_CD) AS F_CODE_NM,
    /*PC장비코드*/ TO_CHAR(TO_DATE(A.ITDC_DT),'YYYY-MM-DD') AS TO_CHAR,
    /*도입일*/ TO_CHAR(TO_DATE(A.ABRG_DT),'YYYY-MM-DD') AS TO_CHAR,
    /*폐기일*/ A.ETC_MTTR_CN AS ETC_MTTR_CN /* 기타사항내용 */,
    /*기타사항*/ F_CODE_NM('1000800',MNT_DVSN_CD) AS F_CODE_NM,
    CASE WHEN SUBSTR(A.SRECS_DEPT_CD,0,3)||'000' = A.SRECS_DEPT_CD THEN F_DPT_INFO(A.SRECS_DEPT_CD,'1') ELSE F_DPT_INFO(SUBSTR(A.SRECS_DEPT_CD,0,3)||'000','1') END AS SUBSTR,
    B.USR_DFN_TXT_3
FROM TBCSPIIT005M A /*PC장비관리테이블*/ ,TBDCMACM013C B WHERE 1=1 AND A.SRECS_DEPT_CD = B.CD(+) AND B.CMM_CD_DVSN_CD(+) = '1000144' AND A.PC_KND_CD = #strPC_KND_CD# AND A.PC_USE_DVSN_CD = #strPC_USE_DVSN_CD# AND A.SRECS_DEPT_CD = #SRECS_DEPT_CD# AND A.PC_EQP_CD = #strPC_EQP_CD# AND UPPER(A.PC_MNG_CD) LIKE '%'||UPPER(#strPC_MNG_CD#)||'%' AND A.PC_SE_CAT_CD = #strPC_SE_CAT_CD# AND A.PSSN_DVSN_CD = #strPSSN_DVSN_CD# AND (F_USR_INFO(A.TRGT_NOP_ENO,'2') LIKE '%'||#strSEARCH_NM#||'%' OR A.TRGT_NOP_ENO LIKE '%'||#strSEARCH_NM#||'%' OR A.PC_MNG_RMK LIKE '%'||#strSEARCH_NM#||'%' OR ETC_MTTR_CN LIKE '%'||#strSEARCH_NM#||'%') AND A.MNT_DVSN_CD = #strMNT_DVSN_CD# ORDER BY B.USR_DFN_TXT_3 ASC