SELECT A.DATA_DMND_SQNO /* 자료요청순번 */,
    /*자료요청순번*/ A.MTRL_RQS_DVSN_CD,
    /*자료요청구분코드*/ A.DMND_YMD AS DMND_YMD /* 요청일자 */,
    /*요청일*/ D.CNSTN_MBCMT_DEPT_NM AS CNSTN_MBCMT_DEPT_NM /* 자문위원부서명 */,
    /*요청자부서명*/ B.USER_NM AS USER_NM /* 사용자명 */,
    /*요청자명*/ A.RQS_TIT_NM,
    /*요청제목*/ C.USER_NM AS USER_NM /* 사용자명 */,
    /*처리담당자명*/ A.GPCD_PRCS_YMD AS GPCD_PRCS_YMD /* 처리일자 */,
    /*처리일자*/ A.RQSTR_ENO AS RQSTR_ENO /* 요청자직원전산번호 */,
    /*요청자전산번호*/ A.PRCS_PIC_ENO AS PRCS_PIC_ENO /* 처리담당자직원전산번호 */,
    /*처리자전산번호*/ A.DMND_DEPT_CD AS DMND_DEPT_CD /* 요청부서코드 */,
    /*요청부서코드*/ A.ATRZ_DOC_NO AS ATRZ_DOC_NO /* 결재문서번호 */,
    /*결재문서번호*/ F.ATRZ_STTS_SECD AS ATRZ_STTS_SECD /* 결재상태구분코드 */,
    /*결재상태코드*/ A.CHF_APV_STA_CD,
    /*과장 승인상태코드*/ (CASE WHEN A.PSN_INFO ='Y' THEN '포함' WHEN A.PSN_INFO ='N' THEN '미포함' ELSE '' END) AS PSN_INFO,
    /*개인정보포함여부*/ A.CVLCPT_ADT_YR AS CVLCPT_ADT_YR /* 민원감사년도 */,
    A.ADT_NO /* 감사번호 */,
    CASE WHEN G.ADT_YR >= '2016' THEN G.ADT_KDCD ELSE '' END AS ADT_YR /* 감사년도 */,
    A.ADT_MTTR_NM /* 감사사항명 */,
    G.ADT_KDCD /* 감사종류코드 */,
    G.ADT_GRNT_NO /* 감사부여번호 */
FROM TBCSPIIT018M A, /*전산개선요청테이블*/ TBDCMACM001M B, /*사용자테이블(요청자)*/ TBDCMACM001M C, /*사용자테이블(처리자)*/ TBDCMACM002M D, /*부서코드(요청자)*/ TBDCMACM002M E, /*부서코드(처리자)*/ TBDCMACM022L F, /*결재문서내역*/ TB_BADDED001M G /*감사사항*/ WHERE A.RQSTR_ENO = B.TRGT_NOP_ENO(+) AND A.PRCS_PIC_ENO = C.TRGT_NOP_ENO(+) AND A.DMND_DEPT_CD = D.SRECS_DEPT_CD(+) AND C.SRECS_DEPT_CD = E.SRECS_DEPT_CD(+) AND A.CVLCPT_ADT_YR = G.ADT_YR(+) AND A.ADT_NO = G.ADT_NO(+) AND A.COPUT_BRD_DVSN_CD = '02' /*게시판구분코드 - 전산개선요청*/ AND A.ATRZ_DOC_NO = F.ATRZ_DOC_NO(+) AND A.CHF_APV_STA_CD IS NOT NULL /*전산개선요청테이블 시스템운영과장 승인상태코드 01승인반려전 02승인 03반려*/ AND TO_NUMBER(A.CHF_APV_STA_CD) = 2 AND A.DMND_YMD &gt;= #strRQS_DTS# AND A.DMND_YMD &lt;= #strRQS_DTE# AND (D.CNSTN_MBCMT_DEPT_NM LIKE '%'||#strSEARCH_NM#||'%' OR E.CNSTN_MBCMT_DEPT_NM LIKE '%'||#strSEARCH_NM#||'%' OR A.RQS_TIT_NM LIKE '%'||#strSEARCH_NM#||'%' OR A.DMND_CN LIKE '%'||#strSEARCH_NM#||'%' OR A.PRCS_CN LIKE '%'||#strSEARCH_NM#||'%') AND B.USER_NM LIKE '%'||#strRQR_NAM#||'%' AND C.USER_NM LIKE '%'||#strHNDL_PEN_NAM#||'%' AND A.PRCS_YMD IS NULL AND A.PRCS_YMD IS NOT NULL AND A.PSN_INFO = #strPSN_INFO#